css:
@namespace  url(http://www.w3.org/1999/xhtml);

@-moz-document domain("api.drupal.org") {

  #page { width: 98% !important; margin-left: 1% !important; margin-right: 1% !important; }
  #page .grid-12 { width: 98% !important; margin-left: 1% !important; margin-right: 1% !important; }
  #page .grid-11 { width: 91% !important; }
  #page .grid-10 { width: 83% !important; }
  #page .grid-9 { width: 75% !important; }
  #page .grid-8 { width: 64% !important; }
  #page .grid-7 { width: 58% !important; }
  #page .grid-6 { width: 50% !important; }
  #page .grid-5 { width: 41% !important; }
  #page .grid-4 { width: 32% !important; }
  #page .grid-2 { width: 16% !important; }
  #page .grid-1 { width: 8% !important; }

  /* links: almost invisible due to too light font color */
  #page a { font-weight: bold; }
  
  
  /* #210615: Mozilla on Mac fix */
  html.js fieldset.collapsible div.fieldset-wrapper { overflow: visible !important; }
  
  
  /* code */
  div.codeblock { background-color: #f8f8fa !important; }
  code { background-color: #f8f8fa !important; font-family: "Courier New","Bitstream Vera Sans Mono",Monaco,"Lucida Console",monospace !important; font-size: 12px !important; padding: 0 !important; line-height: 130% !important; }

  /* Make the main area wider */
  #main { width: 100% !important; }

  #aside { width: 250px; }
  #footer { display: none; }
  .one-sidebar #main { width: 100%;}
  
  /* Move code search to top */
  #search { position: absolute; top: 30px; right: 0; z-index: 100;}
  #search h2 { display: none; }
  
  /* Move nav links to bottom */
  #block-api-navigation { position: fixed; bottom: -1px; left: 0; width: 100%; background: #D7D7C6 !important; }
  #block-api-navigation .block-inner { width: 70%; margin: 0 auto; text-align: 0; text-align: center; padding: 7px 0 0 0; }
  #block-api-navigation h2 { display: none; }
  #block-api-navigation .item-list ul li { display: inline; padding-right: 10px !important; }

  .view-content tr:hover td { background: #D8D8D2 !important; }
}
