css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rateyourmusic.com") {


div [id="issues"],  /* removes ISSUES table */
div [id="issue_expand_btn"],  /* removes the "expand/compact issues" line */
div [class="release_navigation"],   /* removes "next & previous Releases" line */
div [class="release_page_header"],  /* removes REVIEWS table header */
div [id="reviews_shell"],   /* removes REVIEWS table */
div [class="catalog_ownership"],    /* removes the ownership (CD, Digital, etc.) in the RATINGS table */
div [class="catalog_year"], /* removes the empty lines with the catalog year in the RATINGS table */
div [class="catalog_rating_system_comment"] /* removes the comment in the RATINGS table */ 

{
	display: none !important;}

}
