css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://german-bash.org/") {
@charset "utf-8";

body form {display:none !important;}

div#logo {padding:20px !important;}
div.quote_header {background:#ddd !important; color:#aaa !important; font-family:tahoma !important;}
div.quote_header a {text-decoration:none !important; color:#888 !important;}
div.quote_header a:hover {text-decoration:none !important; color:#555 !important;}
div.quote {background:#eee !important; border:0px !important; font-family:Lucida Console !important;}
div.zwei {display:none !important;}
span.user_online {display:none !important;}
div.motd {display:none !important;}
div.header_links {display:none !important;}
div.content {width:100% !important;}
#headerextra2 {width:100% !important;}
.quotebox {padding:0px !important;}


div.header_links > form > span > input {border:none !important;background-color:#DDD !important;}

div#header > div.header_links > iframe,
body > div.content > iframe,
body > div.content > br,
body > div.content > div.quote,
body > div.content > div.motd > iframe,
body > div#footer > span.google_right,
body > script + div,
body > script + div + img {display:none !important;}

}
