css:
@-moz-document url("http://animecalendar.net/") {
body {
  font-family: Calibri !important;
  background-color: rgb(25,25,25) !important;
}
#newsBox, #menu div:nth-child(2), #footer, body div:nth-child(9), body div:nth-child(8), #uservoice-feedback {
  display: none !important;
}
#whereAreWe {
  margin-top: 20px !important;
}
#menu {
  background-color: rgb(60,60,60) !important;
  font-family: Calibri !important;
  height: 18px !important;
}
#menu li, #calendarContent table th, #whereAreWe h1 {
  color: rgb(220,220,220) !important;
}
#menu li a, #whereAreWe a:hover, #whereAreWe a {
  text-decoration: none;
  color: rgb(220,240,240) !important; 
}
#whereAreWe table {
  width: 98% !important;
  border: none !important;
  background-color: rgb(10,10,10) !important;
  margin-bottom:20px !important;
}
table {
  width:98% !important;
}
#calendarContent div.day th {
  background-color: rgb(10,10,10) !important;
}
#calendarContent div.day {
  border:none !important; 
  background-color: rgb(10,10,10) !important;
  margin: 5px !important;
  padding:0px !important;
}
#calendarContent div.day table, #calendarContent div.day_now table{
  width:100% !important;
}
#showInfo th {
  background-color: rgb(10,10,10) !important;
}
#calendarContent a {
  font-size: 12px !important;
}
#calendarContent div.day td.filter h3 a, #calendarContent div.day_now td.filter h3 a {
  color: rgb(139, 172, 194) !important;
}
#calendarContent div.day td.filter small, #calendarContent div.day_now td.filter small {
  color: rgb(85, 108, 122) !important;
}
#calendarContent div.day_now {
  background-color: rgb(40,40,40) !important;
  border: none !important;
  margin:5px !important;
  padding:0px !important;
}
#calendarContent div.day_now th {
  background-color: rgb(40,40,40) !important;
}
#calendarContent div.day_now td.one {
  background-color: rgb(48,48,48) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day_now td.two {
  background-color: rgb(40,40,40) !important;
  padding-top: 5px !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day td.one {
  background-color: rgb(18,18,18) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day td.two {
  background-color: rgb(10,10,10) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day table tbody tr td img, #calendarContent div.day_now table tbody tr td img {
  padding-left:3px !important;
}
#calendarContent div.day td h3 a, #calendarContent div.day_now td h3 a {
  color: rgb(220,220,220) ! important;
  font-family: Calibri !important;
  font-size: 8pt !important;
  font-weight:normal !important;
}
#calendarContent div.day td small, #calendarContent div.day_now td small {
  color: rgb(140,140,140) ! important;
  font-family: Calibri !important;
  font-size: 7pt !important;
  font-weight:normal !important;
}
}
