css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
/*sets text color for ALL text*/
/*
* {
color:rgba(91, 108, 109, 1.0) !important;
}
*/
/*BIG BACKGROUND begin - background for the entire screen*/
body {
background: url('http://www4.picturepush.com/photo/a/13756117/img/Picture-Box/tumblrbg2.png') no-repeat fixed !important;
background-size: auto 100% !important;
background-color: rgba(0,0,0, 1) !important;
background-position: 0% 0% !important;
background-size: cover !important;
}
body:before {
  content: "";
  position: fixed;
  right: 0; top: 0;
  width: 50%; height: 100%;
  background: url('http://www4.picturepush.com/photo/a/13756117/img/Picture-Box/tumblrbg2.png')
    no-repeat fixed top right rgb(183,215,255);
  background-size: auto 100%;
  z-index: -1;
  }
/*BIG BACKGROUND end*/
/*LOGO begin - tumblr logo*/
#logo {
width: /*0px*/199px !important;
height: /*0px*/43px !important;
/*background:url("http://i142.photobucket.com/albums/r84/Gaaras_one_and_only/kurokoslogo_zps0f158631.png") !important;
padding-left: 220px !important;
height: 50px !important;*/
}
.logo_anchor {
  background-image: url("http://i142.photobucket.com/albums/r84/Gaaras_one_and_only/kurokoslogo_zps0f158631.png") !important;
  background-position: -3px -6px !important;
}
/*LOGO end*/
/*ICON BAR begin - new post icons bar - graphic replacement*/
    @-moz-document url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {
.new_post_label{ font-size: 0 !important }
.new_post_label > I:before { content: none !important }
#new_post{ background: url('http://i142.photobucket.com/albums/r84/Gaaras_one_and_only/Kurokoicons_zps07cc81b3.png') center no-repeat white !important }

}

#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://i142.photobucket.com/albums/r84/Gaaras_one_and_only/Kurokotopicons_zps3ca12a4b.png') !important;}




/*ICON BAR end*/
/*ICON BAR BACKGROUND begin - background for the icon bar*/
#new_post {
background-color: rgba(255, 255, 255, 255) !important;
}
/*ICON BAR BACKGROUND end*/
/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
opacity: 1.0 !important;
margin-left: 0px !important;
}
/*AVATAR ARROW end*/
/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: rgba(083, 146, 181, 0.55) !important;
}
/*CONTENT*/
/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(255,255,255,1.0) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(44,71,98, 1.0) !important;
}
/*SELECTION ARROW end*/
/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/
li.selected, li.recessed, a.active, div.post_filter_tabs a {
}
/*GREEN BUTTONS end*/
/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
-moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
background: none !important;
}
/*RADAR end*/
/*NEW CONTENT begin - text post message body background*/
.mceContentBody {
color: rgba(0, 0, 0, 1.0) !important;
background: rgba(255,255,255, 1.0) !important;
}

/*NEW CONTENT end*/
/*DO NOT DELETE THIS BRACKET*/}
