css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com"){

/* background image */
  body#gsr {
    background:url(http://fc05.deviantart.net/fs70/f/2011/320/5/9/jellyfish_queen_by_apofiss-d4gctku.jpg) no-repeat center center fixed !important;
    background-size: cover !important;
  }
  
  body #gbx3, .jsb>center{
    display: none !important;
  }
  
/* Avatar */
.gb_6 {
  border-radius: 15%;
}
  
/* google search results background */
  #mw, #cnt {
    background: white !important;
  }
  
/* footer */
  #fbar, .fbar{
    background:none !important;
    border-top: none !important;
    min-width: 300px !important;
  }

  body[vlink="#61c"] #footer>div {
    background:transparent!important;
    border:none!important;
    color:white!important;
    text-shadow: black 0 1px 3px!important;
  }
  #gbg6 #gbi4t, #gbg4 #gbgs4d, #prm-pt, #ftby, #ftby a:visited, #prm-pt a, .ftl, #footer a {
    color: gainsboro !important;
  }
  
}
