        top: 3px !important;
    }
    #watch_tab_box .view>.nf_detail>.information>.reserve>.icon {
        top: 4px!important;
    }
    /*タグ*/
    #watch_player_top_box .livetag_box .tag_list {
        color: #000000 !important;
        padding: 0!important;
    }
    #watch_player_top_box .livetag_box .tag_list .open {
