    }
    #watch_player_top_box .livetag_box .tag_title {
        padding-bottom: 10px!important;
    }
    #watch_player_top_box .livetag_box {
        position: relative!important;
    }
    #watch_player_top_box .tag_title {
        margin: -1em 5px 5px 0!important;
        position: absolute!important;
        top: 50%!important;
    }
    #watch_player_top_box .cover {
        height: auto !important;
        padding: 3px 0 3px 48px!important;
    }
    #watch_player_top_box {
        margin: 8px auto!important;
        /*min-height:38px!important;
 display: table!important;
 vertical-align: middle!important;*/
    }
    #watch_player_top_box .tag_list li {
        margin: 1px 10px 1px 0!important;
    }
    /*プレイヤー下部*/
    #watch_player_bottom_box .share_right #twitter-widget-0 {
        padding-top: 3px!important;
    }
}
