css:
@-moz-document url-prefix("http://userstyles.org/styles/93804/"), url-prefix("https://userstyles.org/styles/93804/") {

/*Style reviewed for bugs on February 2019*/

#stylish-installed-style-installed { 
text-align: center !important; 
}

#stylish-installed-style-installed:after { 
content: '\a Thank you for installing my style! \a Got some feedback? You know where to find me.' !important; 
font-size: 14px !important; 
display: block !important; 
white-space: pre !important; 
}

div[style*='width: 300px; height: 262px;'] { 
display: none !important; 
}

}

@-moz-document regexp("http://www\.gaiaonline\.com(?!/profiles/).*"), regexp("https://www\.gaiaonline\.com(?!/profiles/).*") {

#content, 
#gaia_header, 
#gaia_content, 
.header_content, 
#gaia_footer p { 
width: 970px !important; 
min-width: 970px !important; 
max-width: 970px !important; 
}

.community #content, 
.community #gaia_header, 
.community #gaia_footer { 
position: relative !important;
margin: auto !important; 
float: none !important; 
width: 970px !important; 
min-width: 970px !important; 
max-width: 970px !important; 
}

.community #gaia_footer { 
text-align: center !important; 
}

.community #gaia_header { 
border-left: 1px solid #000 !important; 
border-right: 1px solid #000 !important; 
}

.community #content { 
border: 1px solid #000 !important; 
border-top: none !important; 
}

#gaia_footer { 
width: 983px !important; 
min-width: 983px !important; 
max-width: 983px !important; 
} 

#dailyReward { 
left: 880px !important; 
}

.goldMessage { 
right: 5px !important; 
}

#Message { 
z-index: +99999 !important; 
}

#offer_banner, 
#other-col-wrapper, 
#bb-advertisement, 
.yui3-overlay-mask, 
.gmodal-theme-offer { 
display: none !important; 
}

#gcash-options-wrapper { 
position: relative !important; 
right: 85px !important; 
}

#store-navigation { 
overflow-y: hidden !important; 
}

#store-navigation:hover { 
overflow-y: auto !important; 
}

#ach_menu_container { 
position: relative !important; 
left: -84px !important; 
top: 1px !important; 
}

#grid_ad, 
#grid_ad_1, 
#grid_ad_2, 
#grid_ad_3, 
#grid_ad_4, 
#grid_ad_5, 
#mygaia-index-ad { 
display: none !important; 
}

#dailytreat:hover:before { 
content: 'Be sure to disable your ad blocker before processing.' !important; 
background: #3A3A3A !important; 
-webkit-border-radius: 20px !important; 
white-space: nowrap !important; 
border: 1px solid #878787 !important; 
border-radius: 20px !important; 
position: relative !important; 
padding: 4px 8px !important; 
color: #FFFFFF !important; 
font-size: 12px !important;  
z-index: 20 !important; 
left: -110px !important; 
top: 0px !important; 
font-weight: normal !important; 
}

#dailytreat:hover:after { 
content: '' !important; 
background: #3A3A3A !important; 
border: 1px solid #878787 !important; 
position: relative !important; 
z-index: 20 !important; 
left: 40px !important; 
top: -3px !important; 
display: block !important; 
width: 15px !important; 
height: 15px !important; 
transform: rotate(45deg) !important; 
-ms-transform: rotate(45deg) !important; 
-webkit-transform: rotate(45deg) !important; 
border-left: 0px !important; 
border-top: 0px !important; 
border-radius: 100px 0px 0px 0px !important; 
}

#dailytreat { 
text-decoration: none !important; 
outline: 0 !important; 
}

#content .gaia_navigation_tabs { 
overflow: hidden !important;; 
width: 940px; 
}

.gaia_tab_orphans .yuimenuitem a[href*='/profiles/'] { 
background: url('http://s.cdn.gaiaonline.com/images/mygaia/tb_mygaia_sprite_60x35_w_locks.jpg?v=1') !important; 
background-repeat: repeat !important; 
background-attachment: scroll !important;
background-position: -537px -0px !important;  
color: transparent !important; 
border: 1px solid #CCC !important; 
border-left: none !important; 
width: 60px !important; 
height: 33px !important; 
position: relative !important; 
display: block !important; 
right: 10px;
}

.gaia_tab_orphans .yuimenuitem a[href*='/profiles/']:hover { 
background-position: -537px -35px !important; 
}

.gaia_tab_orphans .yuimenuitem a[href*='bank.php'] { 
background: url('http://s.cdn.gaiaonline.com/images/gaia_global/gaia_panelnav/ic_shops_icons_ext_new2.png?v=5') !important; 
background-color: #EBEBEB !important; 
background-repeat: repeat !important; 
background-attachment: scroll !important;
background-position: -242px -10px !important;  
color: transparent !important; 
border: 1px solid #CCC !important; 
width: 50px !important; 
height: 33px !important; 
position: relative !Important; 
right: 235px !important; 
} 

.gaia_tab_orphans .yuimenuitem a[href*='bank.php']:hover { 
background: url('http://s.cdn.gaiaonline.com/images/gaia_global/gaia_panelnav/ic_shops_icons_ext_new2.png?v=5') !important; 
background-color: #FFF !important; 
background-repeat: repeat !important; 
background-attachment: scroll !important;
background-position: -242px -10px !important;  
color: transparent !important; 
border: 1px solid #CCC !important; 
width: 50px !important; 
height: 33px !important; 
position: relative !Important; 
right: 235px !important; 
} 

#content .gaia_navigation_tabs .tab_end { 
background: url("http://s.cdn.gaiaonline.com/images/mygaia/rs_tab_endcap_gradient_35.gif") !important; 
background-repeat: no-repeat !important; 
background-attachment: scroll !important; 
background-position: 65px 0px !important; 
}

.gmodal-theme-dailychance { 
background: url("https://dl.dropboxusercontent.com/s/rv1fft1q060k2s8/Daily%20Chance%20BG.png") !important; 
background-position: 0px 0px !important; 
width: 627px !important; 
height: 430px !important; 
border: 2px solid #C41C22 !important; 
border-radius: 5px !important; 
box-shadow: 0px 0px 22px 7px rgba(197,79,77,1) !important; 
}

.gmodal-theme-dailychance .row-top { 
margin-top: -20px !important; 
margin-left: -29px !important; 
}

.gmodal-theme-dailychance .gmodal-close-btn { 
top: -55px !important; 
right: -17px !important; 
}

#dailychance-bottom-row { 
display: none !important; 
}

}

@-moz-document url-prefix("http://www.gaiaonline.com/market/"), 
url-prefix("https://www.gaiaonline.com/market/") {

#yui-main .yui-gb { 
display: none !important; 
}

}

@-moz-document url-prefix("http://www.gaiaonline.com/games/"), 
url-prefix("https://www.gaiaonline.com/games/") {

.grid_rdburman div#ft { 
display: none !important; 
}

}

@-moz-document url-prefix("http://www.gaiaonline.com/profile/"), 
url-prefix("https://www.gaiaonline.com/profile/") {

.grid_rc_container_bottom { 
display: none !important; 
}

}

@-moz-document url-prefix("http://www.gaiaonline.com/forum/"), 
url-prefix("https://www.gaiaonline.com/forum/"), 
url-prefix("http://www.gaiaonline.com/news/"), 
url-prefix("https://www.gaiaonline.com/news/") {

.grid_rc_container_bottom { 
display: none !important; 
}

#yui-main .yui-b, 
#gaia_footer ul, 
#gaia_footer p { 
position: relative !important; 
margin: auto !important; 
float: none !important; 
}

#forum-stats { 
margin-left: 150px !important; 
}

#thread_header { 
margin-left: 1px !important; 
}

.yui3-js-enabled, 
#community { 
background: url('http://s.cdn.gaiaonline.com/images/global_bg/bg2.jpg') !important; 
background-position: 50% 50% !important; 
background-size: auto 100% !important; 
background-attachment: fixed !important; 
}

#gaia_footer { 
position: relative !important; 
margin: auto !important; 
float: none !important; 
width: 970px !important; 
min-width: 970px !important; 
max-width: 970px !important; 
padding: 20px 0 30px 0 !important; 
right: 5px !important; 
}

#gaia_footer ul { 
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6) !important; 
border-radius: 8px !important; 
display: block !important; 
float: left !important; 
margin: 0 0.5% 10px !important; 
min-height: 5em !important; 
padding: 10px 0 10px 0 !important; 
width: 100% !important;
}

#gaia_footer p { 
background: none repeat scroll 0 0 rgba(255, 255, 255, 0.6) !important; 
border-radius: 4px !important; 
clear: both !important; 
margin: 1% 0 0 !important; 
padding: 8px !important; 
width: 1124px !important;
}

#gaia_footer ul a { 
color: #FFF !important; 
}

#gaia_footer ul li { 
border: 0 none  !important; 
display: block  !important; 
float: left  !important; 
margin: 0  !important; 
padding: 0.25em 0 0.25em 4%  !important; 
width: 10% !important; 
}

}

@-moz-document url-prefix("http://www.gaiaonline.com/guilds/"), 
url-prefix("https://www.gaiaonline.com/guilds/") {

.yui3-js-enabled { 
background: url('http://s.cdn.gaiaonline.com/images/global_bg/bg2.jpg') !important; 
background-position: 50% 50% !important; 
background-size: auto 100% !important; 
background-attachment: fixed !important; 
}

#content { 
background: #FFF !important; 
}

#guild_header { 
width: 940px !important; 
}
 
}

