css:
@-moz-document domain("tweetdeck.twitter.com") {

/* Saeed Habibi, Twitter: @saeedhbi */
/* Saeed Habibi, Twitter: @saeedhbi */
html {
    font-family: tahoma !important;
    font-size: 8pt !important;
}

a {
    color: #2FC2EF;
    text-decoration: none !important;
    font-size: 8pt !important;
    font-family: tahoma !important;
}

a:hover,a:focus,a:active {
    color: #618199;
    text-decoration: underline !important;
}

.account-link {
    color: #444 !important;
}

.account-link .username {
    font-size: 1em !important;
}

.txt-mute a:not(:hover):not(:focus) {
    color: #888 !important;
}

p {
    font-family: tahoma !important;
    font-size: 8pt !important;
    padding: 10px 1px 10px 1px !important;
    margin: 0 !important;
    direction: rtl !important;
}

.txt-large {
    font-size: 0.8rem !important;
}

.txt-serif {
    font-family: Tahoma, Georgia,"Times New Roman",serif !important;
}

.column {
    background-color: white !important;
}

.js-app-columns .column {
    width: 33% !important;
}

.column-header,.column-header-temp {
    background-color: #f9f9f9 !important;
}

.column-header-link {
    color: #ccc !important;
}

.mark-all-read-link {
    right: 30px !important;
}

.is-options-open .mark-all-read-link {
    right: 38px !important;
}

.media-full-bleed {
    margin: 0 -10px -8px -10px !important;
}

.triangle {
    margin-left: -3.5px !important;
}
 
    
.compose-open {
    -webkit-transform: translateX(245px) !important;
    -moz-transform: translateX(245px) !important;
    -ms-transform: translateX(245px) !important;
    -o-transform: translateX(245px) !important;
    transform: translateX(245px) !important;
}

.compose-open-margin {
    margin-right: 245px !important;
}

.compose-open-animate {
    -webkit-transition: -webkit-transform 0.2s !important;
    -moz-transition: -moz-transform 0.2s !important;
    -o-transition: -o-transform 0.2s !important;
    transition: transform 0.2s !important;
}  

.app-search-button .icon-search {
    padding-top: 2px !important;
}

.compose-text {
    height: 110px !important;
    font-size: 8pt !important;
    direction: rtl !important;
}

.tweet {
    line-height: 1.88578em !important;
}

.txt-large {
    font-size: 8pt !important;
}

}
