css:
html:before{content: '' !important;
	position: fixed !important;
	outline: 2000px solid rgba(16, 15, 11, .33) !important; 
	z-index: 999999999999999999999 !important;
}

@-moz-document domain("platform.twitter.com"), 
url-prefix("https://www.facebook.com/plugins/"), 
url-prefix("https://plusone.google.com/_"),
url-prefix("https://apis.google.com/_"),
url-prefix("http://www.reddit.com/static/button"),
url-prefix("http://disqus.com/embed/comments"),
url-prefix("http://widgets") {

    html:before
    {
        display: none !important;
    }
}

 @-moz-document url("about:blank") {
  * {
       margin:0px;
       background-color:#ffffff;
    }
 }

 @-moz-document url("about:newtab") {
  * {
       margin:0px;
       background-color:#999896;
    }
 }
