@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http), url-prefix(file) {

a,a:link,a *{
background:#ffffff !important;

color:#0000cc !important;
text-decoration:none !important;
}
a:visited{
background:#ffffff !important;
color:#6500cc !important;
}
body,html{
