css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
body {
    background: #efe !important;
}
.side {
    display: none;
}
.sr-interest-bar {
    height: 94px !important;
}
#siteTable_organic {
    height: 94px !important;
}
#siteTable_organic .nextprev {
    right: 85px !important;
    top: 62px !important;
    width: 55px;
    overflow: hidden;
}
#siteTable_organic .sponsored-tagline {
    right: 220px !important;
}
#siteTable_organic .help {
    right: 145px !important;
    bottom: 2px !important;
}
#header-img {
    margin: 0 !important;
    position: relative;
    top: 7px;
}
#header-bottom-left {
    width: 1140px;
    margin: 0 auto !important;
}
#header-bottom-left ul.tabmenu {
    margin: 0 auto !important;
    display: block !important;
    width: 500px;
    text-align: center;
    position: relative;
    top: -3px;
}
#header-bottom-left ul.tabmenu li.selected {
    position: relative;
    top: 1px;
}
#header-bottom-left ul.tabmenu li a {
    font-size: 16px;
    font-variant: small-caps;
    padding: 3px 6px !important;
    border-radius: 3px 3px 0 0 !important;
}
.content {
    width: 1100px;
    margin: 0 auto !important;
    border: 1px solid #999;
    padding: 20px !important;
    box-shadow: 0 0 15px #888;
    background: #fdfdfd;
}
.content .thing.link {
    border: 1px solid #bbb;
    border-radius: 2px;
    padding: 10px !important;
    box-shadow: 0 0 3px #ddd;
    min-height: 72px;
}
.thing.link:hover {
    background: #efe;
    border-color: #33d;
}
.thing.link .title {
    font-family: Tahoma;
    font-size: 14px !important;
}
.thing.link .title .domain {
    font-size: 9px !important;
    background: #bbb;
    padding: 2px;
    border-radius: 2px;
}
.thing.link .domain, .domain a {
    color: #333 !important;
}
.thing.link .rank {
    margin: 0 10px 0 0 !important;
    text-align: center !important;
    width: 40px !important;
    height: 25px !important;
    line-height: 25px;
    background: #fff;
    border: 1px solid #aaa;
    border-radius: 3px;
    font-size: 12px !important;
    color: #888 !important;
    box-shadow: 1px 1px 0px #cdcdcd;
}
.thing.link .midcol {
    position: absolute;
    width: 42px !important;
    font-size: 10px !important;
    margin: 30px 0 0 0 !important;
}
.entry {
    margin: 0 !important;
    width: 853px;
}
.thumbnail {
    border: 1px solid #666;
    -moz-box-sizing: border-box !important;
    border-radius: 3px;
    float: left !important;
    height: 70px !important;
    line-height: 70px;
    background: #222;
    margin: 0 10px 0 0 !important;
}
.thumbnail img {
    margin: auto 0 !important;
    vertical-align: middle !important;
}
.thumbnail.self {
    background-position: -3px -485px !important;
    background-color: #fff;
    border: 4px solid #ddd;
    -moz-box-sizing: border-box !important;
}
.thing.link .usertext .md {
    border: 1px solid #aaf !important;
    border-radius: 3px !important;
    font-size: 11px !important;
    color: #444;
}
.thing.link {
    position: relative;
}
.thing.link ul.flat-list {
    position: absolute;
    float: right;
    top: 5px;
    right: 5px;
}
.thing ul.flat-list li {
    float: right;
    clear: right;
}
.nsfw-stamp {
    position: absolute;
    bottom: 0;
    left: 0;
}

.comment .midcol, .comment .flat-list {
    display: none !important;
}
.comment .md {
    font-size: 11px !important;
    color: #444;
    max-width: 100% !important;
}
.comment .child {
    border-color: #00f !important;
}
.comment .morecomments {
    margin: 0 0 0 10px;
}
.comment .morecomments a:before {
    content: "+ ";
    font-size: 11px;
    color: #00f !important;
}
.nestedlisting > .thing {
    padding: 5px !important;
}
.nestedlisting > .thing:hover {
    background: rgba(0,0,255,0.05);
}
.comment .child:hover {
    background: rgba(0,0,255,0.05);
}

}
