css:
@-moz-document domain("kongregate.com") {
/* Primary Overrides */
#primarylayout, .category_listing, .category_games_listing, .category_games_listing div, #category .category_games_listing, #category  .category_games_listing div, #category, #category .callout_listing .game, .hoverbox_cont, .hoverbox .game_head, #category .category_badges_listing, .no_subwrap #primarywrap, #home #primarywrap .maincontent, #home.half_midbg #primarywrap .maincontent #feature, .game_details_outer, #play .game_page_wrap, .game_tab_group
	{background: #000 none; background-color:#000; color:#fff; background-image:none;}

.no_subwrap #primarywrap, #home #primarywrap .maincontent
	{background-image:none;}

/* Primary Color Adjust */
#category .callout_listing .game:hover, .btn, body.adjusted .btn, #category .no_favorites, #category .badge_listing_intro, #browsebycategory .category .browse_games .graybg, #extra_games .category .browse_games .graybg, #current_challenges .challengebox, .tab_index, .game_tab_index, .microtabs .tab_title a
	{background-color:#300;}
.game_hover:hover, .game_hover.game_hover_active, #home #recommended_badges_pod .featured_badge, #browsebycategory .category, .cntrNotify, .tab_link.active, .game_tab_item.active .game_tab_link, .microtabs .tab_title a:active, .microtabs .tab_box
	{background-color:#300 !important; border-color:#700 !important;}

.hoverbox *:not(.rating_star), .game, .cntrNotify, .microtabs .tab_box {color:#fff !important;}
.microtabs .tab_title a {font-weight:normal !important; padding:0px 6px;}

/* Grey Color Override */
.game_comments .comments_type, .game_more_games .pod_header, .game_comments .game_comment_form_lower, .game_discussions .game_discussions_links
{background-color:#333 !important; color:#aaa !important;}

/* BG Image Removal */
.game_comments .game_comment_form_lower, .game_discussions .game_discussions_links
{background-image: none;}

/* Links */
a span, li a, a, #home .pod_header h2 a, #home #game_developer_contents_pod h3 a, #home #recommended_badges_pod .featured_badge dt a, #current_challenges .challengebox .challenge_info h3 a, #current_challenges .challengebox .challenge_game_info .title strong a, .badges_browser .botd_content .badge_details a.badge_link, .game_comments .comment .sender_name_link
	{color:#f33; text-decoration:underline; font-size:1.2em; font-weight:bold;}
a:hover span, li a:hover, a:hover, #home .pod_header h2 a:hover, #home #game_developer_contents_pod h3 a:hover, #home #recommended_badges_pod .featured_badge dt a:hover, #current_challenges .challengebox .challenge_info h3 a:hover, #current_challenges .challengebox .challenge_game_info .title strong a:hover, .badges_browser .botd_content .badge_details a.badge_link:hover, .game_comments .comment .sender_name_link:hover
	{color:#fa0;text-decoration:underline;}

em, .badges_browser .botd_content em, .tab_index, .game_tab_index, .microtabs .tab_title a, .microtabs .tab_title a:active
	{color:#a95;}


/* menus and buttons */
.highcontrast
	{color:#fff !important;}

.btn, body.adjusted .btn 
    {
      background: -moz-linear-gradient(top, #530 0%, #700 100%);
      background: -webkit-gradient(linear, left top, left bottom,
      		color-stop(0%, #530), 
      		color-stop(100%, #700));
      background: -webkit-linear-gradient(top, #530 0%, #700 100%);
      background: -o-linear-gradient(top, #530 0%, #700 100%);
      background: -ms-linear-gradient(top, #530 0%, #700 100%);
    }

.btn:hover, body.adjusted .btn:hover 
    {
      background: -moz-linear-gradient(top, #aa0 0%, #a00 100%);
      background: -webkit-gradient(linear, left top, left bottom, 
    		color-stop(0%, #aa0), 
    		color-stop(100%, #a00));
      background: -webkit-linear-gradient(top, #aa0 0%, #a00 100%);
      background: -o-linear-gradient(top, #aa0 0%, #a00 100%);
      background: -ms-linear-gradient(top, #aa0 0%, #a00 100%);
    }

.active .nav_tab_name a, .active .nav_tab_name a:active
	{background-color:#000 !important; color:#ccc !important;}
.active .nav_tab_name a:hover
	{background-color:#710000 !important; color:#fa7 !important; text-decoration:none;}

.tags .tag .term, .tags .tag .term:link, .tags .tag .term:visited
	{background-color:#400; color:#fa7;}

#category .callout_listing .game:hover .tags .term, .game_hover:hover .tags .tag .term, .game_hover.game_hover_active .tags .tag .term
	{background-color:#600; color:#fea;}


#category .category_listing ul.simple_pagination li
	{color:#fff;border-left:0px none; padding:0px 10px;}
#category .category_listing ul.simple_pagination li a:hover
	{color:#fa0;background-color:transparent;}

#subwrap 		{background-color:#111;}
#footer * 		{color:#ccc !important;}
#footer a 		{color:#a00 !important;}
#footer a:hover {color:#fa0 !important;}



#category .badge_row
	{background-color:#171000;color:#ddd;}
#category .badge_row:hover
	{background-color:#210;color:#eee;}
#category .badge_row .badge_details>a
	{color:#c00;}
#category .badge_row .badge_details>a:hover
	{color:#f44;}


#home #registration_showcase_1 #signup_pod_form form
		{background-color:#333;}
	.reusable_signup_form .tos
		{color:#999;}
	.baseform .input_block input, input, select, textarea, #welcome #fields .text_field, form#search dt input
		{border-color:#000 #333 #666; background-color:#444; color:#ddd; font-weight:bold;}
	.baseform .input_block input:focus, input:focus, select:focus, textarea:focus, #welcome #fields .text_field:focus, form#search dt input:focus
		{border-color:#333 #555 #999; background-color:#300; color:#fff; font-weight:bold;}
	.baseform .input_block input.with_error, .with_error, #welcome #fields .text_field, #welcome #fields .with_error, form#search dt .with_error
		{border-color:#900 #f00 #f55; background-color:#600; color:#f33; font-weight:bold;}
	#welcome #fields #welcome_username_label, #welcome #fields #welcome_password_label 
		{color:#ccc;}



body#home #browsebycategory .category {background-color:#100 !important;}
body#home .browse_game_title p {color:#aaa;}
body#home .browse_game_title p a {color:#a00;}
body#home .browse_game_title p a:hover {color:#fa0;}
body#home #browsebycategory .category .browse_games .browse_game_title .browse_developer, #extra_games .category .browse_games .browse_game_title .browse_developer {color:#ccc;}

/* tabs */
.tab_link.active, .game_tab_item.active .game_tab_link, .tab_link, .game_tab_link, .game_tab_nav .game_tab_edge
    {background-color:#500 !important; color:#fff !important; font-weight:bold !important;}
.tab_link.active:before, .game_tab_item.active .game_tab_link:before
	{border-color: transparent transparent #500 #500; font:bold !important;}



/* gamewindow sidebar */

#kong_game_ui ul.main_tabs li.tab a, .cntrToggle, #kong_game_ui .chat_room_tab a:active, #kong_game_ui .chat_room_tab.active a, #kong_game_ui .users_in_room, #kong_game_ui .chat_message_window, #kong_game_ui .chat_message_window *, #high_scores_container ul.high_score_tabs li.high_score_panel_tab a
	{background-color:#300;color:#ddd;}
	#kong_game_ui .user_row .username,#kong_game_ui .chat_message_window, #kong_game_ui .chat_message_window .message
		{color:#999;}
	#kong_game_ui .chat_message_window, #kong_game_ui .chat_message_window p.even, .comment_reply
		{background-color:#411;}
	#kong_game_ui .chat_message_window, #kong_game_ui .chat_message_window .username
		{color:#fff;}


#maingamecontent #chat_container, #kong_game_ui .tabpane, #kong_game_ui ul.main_tabs li.tab a.active,#kong_game_ui .chat_room_tab a , #kong_game_ui .accomplishment_vtabpane_content
	{background-color:#600;color:#fff;}

#kong_game_ui ul.accomplishment_vtabs li.vtab a.active, #kong_game_ui ul.accomplishment_vtabs li.vtab a
{background-color:transparent; color:#fff; border:0px none;}
#kong_game_ui ul.accomplishment_vtabs li.vtab a.active
{color:#f00;}

#high_scores_container .bucket, #kong_game_ui .collapsible_panel a:hover, #game_info .creatorinfo p a:hover, #high_scores_container ul.high_score_tabs li.high_score_panel_tab a, #kong_game_ui .collapsible_panel a:hover, #game_info .creatorinfo p a:hover, #high_scores_container ul.high_score_tabs li.high_score_panel_tab a:hover:hover, #high_scores_container ul.high_score_tabs li.high_score_panel_tab a.active, #high_scores_container table, #high_scores_container table td.username a
	{background-color:#000; color:#fff;}

#kong_game_ui .collapsible_panel a, #game_info .creatorinfo p a, #game_info em
	{color:#aaa; text-decoration:none;}

#kong_game_ui .collapsible_panel a:hover, #game_info .creatorinfo p a:hover, #high_scores_container ul.high_score_tabs li.high_score_panel_tab a:hover
	{color:#fff;}

/* Acheviements area */

body.generic #main p, body.generic #main ul, body.generic #feature ul, #pur .pur_content p, #pur .pur_content h1, #pur .pur_free li
	{color:#ccc !important;}

#pur .pur_content h2, #pur .pur_content h3
	{color:#aaa !important;}

#pur .get_pur ol li
	{color:#eee !important;}

/* hide this sh*t, they keep changing it, and its only ad stuff */
#gamespotlight, .ad-container {display:none;}
}
