css:
@-moz-document url-prefix(http://www.nicovideo.jp/ranking) , url-prefix(
http://www.nicovideo.jp/video_top),url-prefix(https://www.nicovideo.jp/ranking) , url-prefix(
https://www.nicovideo.jp/video_top){

  .videoList01 .videoList01Wrap
  {
    width: 160px!important;
  }

  .video .itemThumbBox
  {
    width: 160px!important;
    height: 100px!important;
  }

  .video .itemThumb
  {
    width: 160px!important;
    height: 100px!important;
    display: block!important;
  }

  .video .itemThumb .itemThumbWrap
  {
    background-size: 160px 100px!important;
    height: 100px!important;
  }

  .video .itemThumb .itemThumbWrap img
  {
    width: auto!important;
    height: auto!important;
    margin: 0 auto!important;
    max-height: 100px!important;
    max-width: 160px!important;
  }

  .video .itemThumb .itemThumbWrap img.noImage
  {
    height: 100px!important;
    max-height: 100px!important;
  }

  .video .uadlevel1 .itemThumbWrap:before,
.video .uadlevel2 .itemThumbWrap:before,
.video .uadlevel3 .itemThumbWrap:before,
.video .uadlevel4 .itemThumbWrap:before,
.video .uadlevel5 .itemThumbWrap:before,
.video .uadlevel6 .itemThumbWrap:before,
.video .uadlevel7 .itemThumbWrap:before,
.video .uadlevel8 .itemThumbWrap:before,
.video .uadlevel9 .itemThumbWrap:before,
.video .uadlevel10 .itemThumbWrap:before
  {
    background-size: 160px 100px!important;
    height: 100px!important;
  }

  .videoList01 .item.videoRanking[data-nicoad-grade="gold"]
  {
    background: -moz-linear-gradient(top, #F6DA57, #EDCC45 50%, #CD9A01)!important;
    background: -webkit-gradient(linear, left top, left bottom, from(#F6DA57), color-stop(0.5, #EDCC45), to(#CD9A01))!important;
  }

  .videoList01 .item.videoRanking[data-nicoad-grade="silver"]
  {
    background: -moz-linear-gradient(top, #D2D7D9, #B0B9BD 50%, #ABB5B9)!important;
    background: -webkit-gradient(linear, left top, left bottom, from(#D2D7D9), color-stop(0.5, #B0B9BD), to(#ABB5B9))!important;
  }

  .videoList01 li.item.videoRanking
  {
    border-bottom: 1px solid #444444 !important;
    padding-top: 5px!important;
  }

  .videoList01 .itemContent .itemTitle
  {
    margin-top: 0!important;
  }

  .videoList01 .item.videoRanking .rankingNumWrap .rankingPt
  {
    color: #000000!important;
  }

  .video .itemData *
  {
    color: #000000!important;
  }

  .video .itemData span,
.video .itemData a
  {
    color: #0559f2!important;
  }

  div#nicoads
  {
    display: none !important;
  }

  .container.columns .column.sub
  {
    display: none!important;
  }

  .column700-300 .main
  {
    width: 1024px!important;
    min-width: 1024px!important;
    max-width: 1024px!Important;
  }

  .videoList01 .itemContent .itemTitle.ranking
  {
    width: 700px!important;
  }

  .videoList01 .itemContent .itemDescription.ranking
  {
    height: auto!important;
    width: 690px!important;
  }

  .videoList01 .itemContent .itemComment.ranking
  {
    height: 28px!important;
    width: 600px!important;
    font-weight: normal!important;
    line-height: 170%!important;
  }

  .videoList01 .videoList01Wrap
  {
    margin-right: 30px!important;
  }

  .video [data-nicoad-grade="silver"] .itemThumbWrap:before
  {
    height: 100px!important;
    background: url(http://nicovideo.cdn.nimg.jp/web/images/modules/frame/frame_nicoad_silver.svg) no-repeat!important;
    transform: scale(1.0,1.11111);
    filter: saturate(500%)!important;
    background-size: 160px 100px!important;
  }

  .video [data-nicoad-grade="gold"] .itemThumbWrap:before
  {
    background: url(http://nicovideo.cdn.nimg.jp/web/images/modules/frame/frame_nicoad_gold.svg) no-repeat!important;
    transform: scale(1.0,1.11111);
    filter: saturate(1000%)!important;
    height: 100px!important;
    background-size: 160px 100px!important;
  }

}










@-moz-document url-prefix( http://www.nicovideo.jp/search/) , url-prefix(http://www.nicovideo.jp/tag/),url-prefix( https://www.nicovideo.jp/search/) , url-prefix(https://www.nicovideo.jp/tag/) {
/*広告とか消去*/
div#nicoads {
    display: none !important;
}
.container.columns .column.sub {
    display: none!important;
}
.column700-300 .main {
    width: 1024px!important;
    max-width:1024px!important;
    min-width:1024px!important;
}

/*検索ボックス常時表示*/
.main {
    padding-right: 0!important;
}
div.searchOption.hidden {
    display: block!important;
    height: 150px!important;
    padding-top: 12px!important;
    margin-top: 12px!important;
    padding-bottom: 12px!important;
    margin-bottom: 16px!important;
}
.formSearch {
    border: 1px solid #999F9F!important;
    border-radius: 3px 3px 3px 3px!important;
    box-shadow: none!important;
    height: 100px!important;
}
.formSearch .seachFormA {
    margin-top: 24px!important;
}
.formSearch .sugoiSerach {
    top: 52px!important;
}
.searchOption {
    border: 1px solid #E5E5E5!important;
}
.tagCaption .tagListBox {
    border: 1px solid #E5E5E5!important;
    border-radius: 3px 3px 3px 3px!important;
}
.searchOption .optionList.sort{
width:200px!important;
}
/*4列時交互色つけ*/
.video .item.videoList04:nth-child(12n+1),
.video .item.videoList04:nth-child(12n+3),
.video .item.videoList04:nth-child(12n+5),
.video .item.videoList04:nth-child(12n+8),
.video .item.videoList04:nth-child(12n+10),
.video .item.videoList04:nth-child(12n),
.video.videoList04 .item:nth-child(12n+1),
.video.videoList04 .item:nth-child(12n+3),
.video.videoList04 .item:nth-child(12n+5),
.video.videoList04 .item:nth-child(12n+8),
.video.videoList04 .item:nth-child(12n+10),
.video.videoList04 .item:nth-child(12n){
    background: #DCE8FA;
}
.video .item.videoList04,.video.videoList04 .item {
    margin-bottom: 0!important;
    margin-right: 0!important;
    padding: 4px!important;
    width: 169px!important;
    height: 300px!important;
    border-bottom: 1px solid #BBBBBB!important;
    border-right: 1px solid #BBBBBB!important;
    overflow: hidden!important;
}
.video .itemTime {
    text-align: center!important;
}
.video .item.videoList04:nth-child(4n+1) ,.video.videoList04 .item:nth-child(4n+1){
    clear: none!important;
}
/*4列時:NGリスト対応*/
.video .item.nrn-4-column-item:nth-child(12n+1),
.video .item.nrn-4-column-item:nth-child(12n+3),
.video .item.nrn-4-column-item:nth-child(12n+5),
.video .item.nrn-4-column-item:nth-child(12n+8),
.video .item.nrn-4-column-item:nth-child(12n+10),
.video .item.nrn-4-column-item:nth-child(12n) {
    background: #DCE8FA;
}
.video .item.nrn-4-column-item {
    margin-bottom: 0!important;
    margin-right: 0!important;
    padding: 4px!important;
    width: 169px!important;
    height: 300px!important;
    border-bottom: 1px solid #BBBBBB!important;
    border-right: 1px solid #BBBBBB!important;
    overflow: hidden!important;
}
.video .itemTime {
    text-align: center!important;
}
.video .item.nrn-4-column-item:nth-child(4n+1) {
    clear: none!important;
}
.videoList02 .item, .video .item.nrn-4-column-item{
float:left!important;
}

/*NGリスト縮小対応*/
.video .nrn-reduce  .itemThumb{
width:80px!important;
height:45px!important;
}
.video .nrn-reduce .itemThumb .itemThumbWrap {
    background-size: 80px 45px!important;
    height: 45px!important;
}
.video .nrn-reduce .itemThumbBox{
width:80px!important;
height:45px!important;
}
.video .nrn-reduce .itemThumb .itemThumbWrap img{
width:80px!important;
height:45px!important;
max-height: 45px!important;
 max-width: 80px!important;
}

/*文字色*/
.video .itemData * {
    color: #000000!important;
}
.video .itemData span,
.video .itemData a {
    color: #0559f2!important;
}
.nrn-description-open-button{
background:none!important;
}
.video .itemData span.nrn-movie-info-toggle{
    color: #000000!important;
}

