css:
@-moz-document domain(www.sixprizes.com)
{
    body {
        background: none repeat scroll 0 center #131313 !important;
    }
    h1, h2, h3, h4, h5, h6 {
    color: #DDDDDD !important;
    }
    #logo-area {
        background: url("http://a.pomf.se/2Pg1.png") no-repeat !important;
        float: left !important;
        margin: 0 20px 0 0 !important;
    }

    #logo-area a {
        box-shadow: 0 0 10px #000000 !important;
    }

    #logo-area img{
      visibility:hidden !important;
    }
    a:hover {
        color: #DDDDDD !important;
    }
    #nav li a, #login-nav li a {
        color: #DDDDDD !important;
        text-shadow: none !important;
    }
    #footer p {
        color: #DDDDDD !important;
        text-shadow: none !important;
    }
    #footer a {
        color: #DDDDDD !important;
        text-decoration: underline !important;
        text-shadow: none !important;
    }
    .post-info {
        color: #DDDDDD !important;
    }
    .home .post-info a, .archive .post-info a, .page-template-page_blog-php .post-info a {
        color: #DDDDDD !important;
    }
    .home #content, .archive #content .wrap, .page-template-page_blog-php #content {
        background: none repeat scroll 0 0 #131313 !important;
        box-shadow: 0 0 10px #000000 !important;
        padding: 20px 20px 10px !important;
    }
    .home .hentry, .archive .hentry, .page-template-page_blog-php .hentry {
        border-bottom: 1px dotted #DDDDDD !important;
        box-shadow: none !important;
        padding: 0 0 20px !important;
        background-color:#131313 !important;
    }

    .widget .post-info {
        background-color: #131313 !important;
        color: #DDDDDD !important;
    }
    .widget .post-info a {
        color: #DDDDDD !important;
    }
    .sidebar .widget {
        color: #DDDDDD !important;
        background-color: #131313 !important;
        box-shadow: 0 0 10px #000000 !important;
    }
    .dsq-widget-comment p {
        color: #DDDDDD !important;
        display: inline !important;
    }
    #login-nav li.search .s {
        color: #DDDDDD !important;
        text-shadow:none !important;
        width: 125px !important;
    }
    #login-nav li.search .s:hover {
        background: none repeat scroll 0 0 #131313 !important;
    }
    #login-nav li.search .s:focus {
        background: none repeat scroll 0 0 #131313 !important;
    }

    .widget-area .textwidget {
        color: #DDDDDD !important;
    }

    .widget ul li {
    border-bottom: 1px dotted #DDDDDD !important;
    }
    input {
        color: #DDDDDD !important;
    }

    body, h1, h2, h2 a, h2 a:visited, h3, h4, h5, h6, p, select, textarea {
    color: #DDDDDD !important;
    }

    .hentry {
    background: none repeat scroll 0 0 #131313 !important;
    box-shadow: 0 0 10px #000000 !important;
    }

    blockquote {
    background: none repeat scroll 0 0 #131313 !important;
    }

    #smart-archives-list h4 a {
    color: #DDDDDD !important;
    }


    body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, button, textarea, p, blockquote, th, td {
    background-color: #131313 !important;
    } 

    #header {
    background-color: #131313 !important;
    }

    .navTabs {
    background-color: #131313 !important;
    border-color: #65A5D1 #65A5D1 #032A46 !important;
    border-style: none !important;
    }


    .footer .pageContent {
        background-color: #131313 !important;
        }
    .navTabs .navTab.selected .tabLinks {
        background: none repeat scroll 0 0 #131313 !important;
    }
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
        background-color: #131313 !important;
        border-bottom: 1px solid #D7EDFC !important;
        padding: 0 !important;
    }
    .sectionMain {
        background-color: #131313 !important;
        border: none !important;
    }


    .nodeList .categoryStrip {
        background: none repeat scroll 0 0 #131313 !important;
        border-bottom: none !important;
        border-radius: none !important;
        border-top: none !important;

    }
    #content .pageContent {
        background-color: #131313 !important;
    }

    .node .nodeLastPost {
        background: none repeat scroll 0 0 #131313 !important;
    }

    .secondaryContent {
        background: none repeat scroll 0 0 #131313 !important;
        border-bottom:none;
        padding: 10px !important;
    }


    .breadcrumb {
        border:none !important;
        background: none repeat scroll 0 0 #131313 !important;

    }
    .breadcrumb .crust a.crumb {
        background-color: #222324 !important;
        border-bottom:none !important;
    }

    .breadcrumb .crust a.crumb > span {
        background-color: #222324 !important;
    }

    .breadcrumb .crust .arrow span {
        -moz-border-right-colors: #222324 !important;
        border-color: rgba(0, 0, 0, 0) #000000 rgba(0, 0, 0, 0) #222324 !important;
    }
    #SignupButton .inner {
        background: none repeat scroll 0 0 #131313 !important;
    }

    .navTabs .navTab.selected .tabLinks a {
        line-height: 10px !important;
    }
    .am-form div.element-title label {
        color: #DDDDDD;
    }

    .secondaryContent {
        border-bottom: none !important;
    }

    .formPopup .controlsWrapper {
    background: none !important;
    }

    .button {
    background: none !important;
    border:none !important;
    box-shadow: none !important;
    text-shadow:none !important;
}
    a.scan-link {
        color: #DDDDDD !important;
    }
    input[type="submit"]{
     color:#000000 !important;
    }






}
