css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4pda.ru") {

/* ник */
.comment-box .comment-list .nickname:after {
    content: ":" !important;
}

.comment-box .comment-list .nickname {
	float: none !important;
	display: inline !important;
}

/* верхняя строка */
.comment-box .comment-list .heading, .comment-box .comment-list .h-meta, .comment-box .comment-list .karma {
	float: left !important;
}
.comment-box .comment-list .heading {
	margin-bottom: -12px !important;
}

/* ответить */
*[id^="comment-form-reply-"] {
    display: inline !important;
    float: right !important;
}

.comment-box .content {
	margin: 0 !important;
	padding-top: 5px !important;
}
.icon-link:before {
    content: "#"
}
.comment-box .comment-list {
    margin-top: 5px !important;
	margin-bottom: 18px !important;
}
.comment-box .comment-list .h-meta, .comment-box .comment-list .karma {
    line-height: 26px !important;
}

[class^="icon-"]:before, [class*=" icon-"]:before {
	margin: 0 !important;
}
.content > br {
    display: none !important;
}
.comment-box .heading {
	border: none !important;
}
}
