css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {
.wallImage, .chipWallListItemList {
  display: none !important;
}

.videoThumb {
  display: none;
}

.videoMainInfoContainer {
  display: block !important;
  background: #fafafa;
  height: 6.65em;
  width: 51.7em;
  box-sizing: border-box;
  padding: 1em;
  border: 1px solid #cfcfcf;
  position: absolute;
  bottom: -17.5em;
  left: 0;
}

.searchContainer .searchText button {
  margin-top: -2em !important;
  background-color: white;
  height: 26px;
  border-bottom: 2px solid #ccc;
}
    
#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer {
  min-width: 49.6em;
  width: 49.6em !important;
  position: relative;
  top: 1.82em;
  height: 4.7em !important;
  box-shadow: none;
  left: -1em;
}

.channelVideo #videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer {
  top: 2.15em;
}

.size_normal #videoTagContainer {
  min-width: 94.8em !important;
}

#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer .tagInner #videoHeaderTagList {
  padding-left: 1em;
}

#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer .toggleTagEditInner {
  color: transparent;
}

#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer .toggleTagEdit {
  background: transparent;
  height: auto;
  width: auto;
  bottom: 0;
  left: auto;
  right: -1em;
  margin: 0;
}

#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer .tagInner #tagEditContainer {
  margin: 0;
  padding: 0;
}

.size_small #content {
  padding-bottom: 1.5em;
}

.size_small.content-fix #content {
  padding-bottom: 0;
}

.size_small #videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer {
  display: none !important;
}

.size_normal #videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer {
  width: 67em !important;
  min-width: 67em !important;
}
    
.size_normal .videoMainInfoContainer {
  width: 69em !important;  
}

.size_small .videoMainInfoContainer {
  display: none !important
}

.size_small #videoMenuTopList {
  display: none !important;
}

.size_normal #videoMenuTopList {
  bottom: -68.5em !important;
  min-width: 96.7em !important;
}

.size_normal #videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer + #videoMenuWrapper #videoMenuTopList {
  bottom: -58.5em !important;
}

.nicoruWrapper.tagControlShow, #videoTagContainerPin {
  display: none !important;
}

#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation + #videoTagContainer + #videoMenuWrapper #videoMenuTopList {
  bottom: -51em !important;
  left: -1em;
  height: 5em;
}

#videoTagContainer {
  background-color: #fafafa;
  width: 1008px !important;
  position: absolute;
  left: 0;
  bottom: -9.3em;
  border: 1px solid #cfcfcf;
  box-shadow: 0 2px 3px #cfcfcf;
  padding: 1em;
}

#videoHeaderMenu .searchContainer {
  margin-top: -5em;
  right: -1em;
}

#videoHeaderMenu .searchContainer .searchText {
  background: #fff;
  border: 1px solid #cfcfcf;
  border-radius: 0;
  box-shadow: none;
  opacity: .7;
}

.userProfile {
  position: absolute;
  bottom: 1.5em;
  left: 2em;
  border: 1px solid #cfcfcf;
  background: #f4f4f4;
}

.readMore.videoDetailOpenButton {
  display: none !important;
}

#videoHeader:not(.infoActive) {
  margin-bottom: 0 !important;
  height: 10em;
}

#videoHeader {
  border: 1px solid #cfcfcf;
  padding: 1em;
  background: #fafafa;
  margin: 5em auto 10em;
  box-shadow: 0 2px 3px #cfcfcf;
}

#videoHeader.menuOpened #videoMenuWrapper {
  margin-bottom: 0;
}

#videoHeader #videoHeaderDetail {
  margin-top: 0 !important;
}

#videoHeader #videoHeaderDetail {
  width: 100%;
  margin: 0;
  border-bottom: 1px dotted #cfcfcf;
}

#videoHeaderDetail h2 {
  max-width: 100%;
}

#topVideoInfo .videoDescription, .shortVideoInfo {
  box-sizing: border-box;
  width: 100%;
  padding: 2em 0 0 2em;
  margin-bottom: 8em;
}

#topVideoInfo .videoStats {
  width: 35em;
  font-size: 12px;
  position: relative;
  top: -1.71em;
  left: 18em;
  color: #666;
}

#topVideoInfo .videoStats span {
  font-weight: normal !important;
}

#topVideoInfo .ranking {
  margin-top: .3em;
  vertical-align: middle;
  line-height: 1.5;
}

#topVideoInfo .ranking + .dicIcon {
  bottom: 2.15em !important;
}

#topVideoInfo [style] + .dicIcon {
  bottom: 1em !important;
}



#videoHeader .shortVideoInfo .shortDescription {
  max-width: 845px !important;
}

#videoHeader .toggleDetailExpand {
  position: absolute;
  top: auto;
  bottom: 1.3em;
  right: 1em;
}

#videoHeader #videoMenuWrapper {
  height: 0px !important;
  overflow: visible;
  position: absolute;
  top: auto;
  bottom: 0;
}

#videoMenuWrapper {
  overflow: visible !important;
}

.videoMenuToggle {
  display: none !important;
}

#videoMenuTopList li.videoMenuList {
  display: block !important;
}

#videoMenuTopList {
  width: 1032px !important;
  height: 75px;
  padding: 0;
  border: 1px solid #cfcfcf;
  background: #fafafa;
  padding-top: 1em;
  margin-bottom: 1em;
  position: absolute;
  top: auto;
  bottom: -61em;
  height: 5em;
  left: -1em;
}

#videoMenuTopList li.videoMenuList {
  float: left !important;
}
#videoMenuTopList li.videoMenuList:first-of-type {
  margin-left: 0.5em;
}

#playerContainerWrapper {
  margin-bottom: 8em;
}

#videoMenuTopList li.videoMenuList {
  width: 111px;
}

#playerAlignmentArea.size_medium #playerContainer.controll_panel.oldTypeCommentInput #playerTabWrapper {
  width: 27.5em;
  right: -1em;
}

#playerTabContainer .playerTabHeader .playerTabItem {
  width: 178px;
}

.commentTableContainer {
  width: 344px !important;
  background: #fafafa;
}

#playerCommentPanel .section .commentTable .commentTableHeaderWrapper {
  background: #515151;
}

#appliPanel {
  width: 357px !important;
  background: -moz-linear-gradient(top, #232323 0%, #131313 100%);
  background: -webkit-linear-gradient(top, #232323 0%, #131313 100%);
}


.commonsTreeExists #topVideoInfo .parentVideoInfo {
  position: absolute;
  top: -4em;
  display: none;
}

.ranking, .dicIcon {
  position: relative;
  bottom: 2em;
}

#topVideoInfo .videoMainInfoContainer .hiddenInfo {
  position: absolute;
  top: 4.4em;
  left: 6em;
}

#topVideoInfo .hiddenInfo .hiddenInfoTabContent {
  position: relative;
  left: -12em;
  bottom: 1em;
}

.videoStats {
  position: relative;
  bottom: 1.9em;
  left: 16em;
}

.extraVideoTrigger.trigger {
  position: relative;
  bottom: 2em;
  right: 12em;
}

.socialLinkGoogle {
  width: 85px !important;
}

.socialLinkFacebook, .socialLinkTwitter {
  width: 120px !important;
}

#videoHeader .toggleDetailExpand .open, #videoHeader .toggleDetailExpand .close {
  text-decoration: none;
}

#videoHeader .toggleDetailExpand {
  z-index: 10;
}

.videoMenuToggle.videoMenuToggleButton.firstNavigation {
  display: none !important;
}

#textMarquee {
  display: none !important;
}

#playerCommentPanel .section .commentTable span.comment {
  width: 270px !important;
}

#playerAlignmentArea.size_medium #playerContainer.controll_panel.oldTypeCommentInput #playerTabWrapper {
  height: 554px;
}

.size_normal #playerTabWrapper {
  height: 650px !important;
  width: 27.5em;
  position: absolute;
  right: -1em;
}

#playerTabContainer {
  background: #fafafa;
  border: 1px solid #cfcfcf;
}

#playerTabContainer .playerTabHeader .playerTabItem.active {
  background: #fafafa;
}

#playerTabContainer .playerTabHeader .playerTabItem {
  border-left: 1px solid #cfcfcf;
  border-bottom: 1px solid #cfcfcf;
  background: #f4f4f4;
}

#playerTabContainer .playerTabHeader .playerTabItem:hover {
  background: #fafafa;
}

.nicoSpotAds, .playerTabAds {
  display: none !important;
}

.size_medium .panel_ads_shown #playerTabContainer.has_panel_ads .playerTabContent {
  height: 90.8%;
}

.size_normal .panel_ads_shown #playerTabContainer.has_panel_ads .playerTabContent {
  height: 92.7%;
}

#playlist {
  background: #f4f4f4;
  display: none;
}

#videoExplorerExpand {
  display: none;
}

#videoExplorerExpand .expandButton {
  color: #515151;
}

#outline {
  display: none !important;
}

.ichiba_mainitem {
  background: #fafafa;
  box-sizing: border-box;
  padding: 1em !important;
  border: 1px solid #cfcfcf;
}

#ichibaMain .balloonUe, #ichibaMain .balloonShita img {
  width: 153px !important;
}

#ichibaMain .balloonShita img {
  margin-top: 0.5em;
}

.thumbnail {
  margin-top: 0.5em !important;
}

#footer {
  display: none;
}

body.size_normal:not(.full_with_browser) #playerNicoplayer {
  position: absolute;
  left: -1em;
}

body:not(.full_with_browser) #playerNicoplayer {
  position: absolute;
  top: 7.15em;
}

.size_small #playerNicoplayer {
  position: static !important;
}

.size_small #playerNicoplayer:before {
  display: none !important;
}

body.size_normal:not(.full_with_browser) #playerNicoplayer:before {
  height: 2.8em !important;
  top: 37.8em !important;
}

body:not(.full_with_browser) #playerAlignmentArea.size_medium #playerNicoplayer {
  position: relative;
  right: 1em;
}

#wallImageContainer {
  top: .8em !important;
}

.size_medium #wallImageContainer {
  height: 49.1em !important;
}

.size_normal #wallImageContainer {
  height: 56.67em !important;
}

/* 前へ次へボタンを隠す */
body:not(.full_with_browser) #playerNicoplayer:before {
  content: "";
  display: block;
  width: 7.3em;
  position: absolute;
  background: #1b1b1b;
  z-index: 100;
  left: 4.5em;
  background: -moz-linear-gradient(top, #232323 0%, #131313 100%);
  background: -webkit-linear-gradient(top, #232323 0%, #131313 100%);
  height: 2.7em;
  top: 30.4em;
}

.full_with_browser #playerNicoplayer:before {
  content: "";
  display: block;
  width: 7.3em;
  position: absolute;
  background: #1b1b1b;
  z-index: 100;
  left: 4.5em;
  background: -moz-linear-gradient(top, #232323 0%, #131313 100%);
  background: -webkit-linear-gradient(top, #232323 0%, #131313 100%);
  height: 2.7em;
  bottom: 2.4em;
}
}
