css:
@-moz-document url-prefix("http://www.nicovideo.jp/ranking") {
.videoList01 .itemContent {
    position: relative;
    display: block!important;
}

.videoList01 .itemContent .itemTitle.ranking {
    position: absolute;
    top: 0;
    left: 0;
    display: inline-block;
    width: 87.7%;
    margin: .5em 0 0 5.37em;
}

.itemTitle.ranking + .wrap,
.itemTitle.ranking + .wrap + .itemData {
    position: absolute;
    left: 0;
    display: inline-block;
}

.itemTitle.ranking + .wrap {
    top: 3em;
    width: 64%;
    margin-left: 19.37em;
}

.itemComment.ranking {
    margin-top: .5em;
}

.itemTitle.ranking + .wrap + .itemData {
    font-size: 1.2em;
    top: 6.75em;
    width: 63.9%;
    margin-left: 16.2em;
}

.itemTitle.ranking + .wrap + .itemData .list {
    position: absolute;
    right: 0;
}

.video .itemThumbBox {
    width: 167px;
    height: 125px;
}

.video .itemTime {
    font-size: 1em;
    position: absolute;
    bottom: 1.15em;
    left: 35.5em;
    width: 17em;
}

.videoList01 .item.videoRanking {
    padding: 0;
    border: 1px solid #cfcfcf;
    background: #fefefe;
    -webkit-box-shadow: 0 2px 3px #cfcfcf;
    box-shadow: 0 2px 3px #cfcfcf;
}

.videoList01 .item.videoRanking:first-of-type {
    margin-bottom: .5em;
}

.videoList01 .item.videoRanking:not(:first-of-type):not(:last-of-type) {
    margin: .5em 0;
}

.videoList01 .item.videoRanking:last-of-type {
    margin-top: .5em;
}

.sub {
    background: none!important;
}

.rankingNumWrap {
    height: 10em;
    padding-top: 2em!important;
}

.video .itemThumb {
    position: relative;
    top: 2.7em;
    left: .5em;
}

.videoList01 .item.videoRanking.uadlevel6,
.videoList01 .item.videoRanking.uadlevel7,
.videoList01 .item.videoRanking.uadlevel8,
.videoList01 .item.videoRanking.uadlevel9,
.videoList01 .item.videoRanking.uadlevel10 {
    background: #fefefe;
}

.videoList01 .item.videoRanking.uadlevel6 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel7 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel8 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel9 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel10 .rankingNumWrap {
    background: #fdf1cd;
}

.videoList01 .item.videoRanking.uadlevel1,
.videoList01 .item.videoRanking.uadlevel2,
.videoList01 .item.videoRanking.uadlevel3,
.videoList01 .item.videoRanking.uadlevel4,
.videoList01 .item.videoRanking.uadlevel5 {
    background: #fefefe;
}

.videoList01 .item.videoRanking.uadlevel1 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel2 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel3 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel4 .rankingNumWrap,
.videoList01 .item.videoRanking.uadlevel5 .rankingNumWrap {
    background: #e5e8ea;
}

.rankingHeader .rankingBtn .save {
    border: 1px solid #cfcfcf;
    background: #fefefe;
}

.rankingHeader .rankingBtn .rss {
    border: 1px solid #cc5300;
    background: #ff6800;
}

.header,
.rankingHeader {
    background: #fafafa;
}

.mainMenu,
.videoSearchInput {
    background: #fefefe;
}

.videoList01 .uadlevel6 .adComment,
.videoList01 .uadlevel7 .adComment,
.videoList01 .uadlevel8 .adComment,
.videoList01 .uadlevel9 .adComment,
.videoList01 .uadlevel10 .adComment,
.videoList01 .uadlevel1 .adComment,
.videoList01 .uadlevel2 .adComment,
.videoList01 .uadlevel3 .adComment,
.videoList01 .uadlevel4 .adComment,
.videoList01 .uadlevel5 .adComment {
    margin-top: -17px;
}

}
