css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hamster.cc") {
/* Scrollbar */

::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-track {
    background: #Dadada;
    -webkit-box-shadow: inset 1px 1px 2px rgba(0,0,0,0.1);
}
::-webkit-scrollbar-thumb {
    background: #858585;
    -webkit-box-shadow: inset 1px 1px 2px rgba(0,0,0,0.3);
}
::-webkit-scrollbar-thumb:hover {
    background: #656565;
}
::-webkit-scrollbar-thumb:active {
    background: #505050;
    -webkit-box-shadow: inset 1px 1px 2px rgba(0,0,0,0.5);
}


body{
	background: #f0f0f0;
}

#header{
	background: url(http://i.imgur.com/cP0425A.jpg) no-repeat !important;
}

#outer{
	margin: 10px auto;	
  	background: #f0f0f0;
  	width: 1000px;
}

#upbg{
	background: #f0f0f0;
}

#menu{
	background: #303030;
  	padding: 0 20px;
  	position: relative;
  	height: 60px;
}

#menu ul{
  	position: absolute;
  	top: 20px;
  	left: 20px;
  	line-height: inherit;
}

#menu ul li{
	line-height: inherit;
}

#search{
	position: absolute;
  	top: 69px;	
 	 right: 20px;
 	
}

#search br{
	display: none;
}

#search label{
	display: none;
}

#search input[type=text]{
	border: none;
	height: 18px;
	width: 180px;
	border-radius: 2px;
	border-bottom: 2px solid #d8d8d8;
	overflow: hidden;
	background: #fafafa;
	outline: none;
  	padding-right: 30px;
}

#search input[type=text]:focus{
	border: none;
  	border-bottom: 2px solid #d8d8d8;
}

#search input[type=submit]{
  	border: none;
	height: 24px;
	background: #d8d8d8;
	right: 0px;
	top: 0px;
	position: absolute;
	border-radius: 0 2px 2px 0;
	width: 30px;
}

#search input[type=submit]:hover{
	background: #b8b8b8;
  	cursor: pointer;
}

#content{
	width: 680px;
}

#sidebar{
	width: 260px;
}

#sidebar h2{
	background: none;
  	padding: 0;
}

#sidebar ul ul{
  	margin-left: 10px;
  	position: relative;
}

#sidebar ul ul li{
	float: left;
  	clear: both;
}

#sidebar ul ul li a, .recentcomments a{
	float: left;
  	clear: both;
  	margin-bottom: 1px !important;
}

#sidebar ul ul li a:hover{
  	margin-bottom: 0px !important;
}

.post-stats{
	float: left;
  	clear: both;
}

.fb-like-box{
	width: 260px;
  	height: 100px;
}
  
.fb-like-box span{
	width: 260px !important;
}
    
.fb-like-box iframe{
	width: 260px !important;
}


#search-2{
	display: none;
}

.post{
  	background: #ffffff;
	overflow: hidden;
	border-radius: 3px;
  	border: 1px solid #d8d8d8;
 	border-top: none;
	border-bottom: 2px solid #d8d8d8;
}
  
.post .header{
	margin-top: 10px;
    background: none !important;
}
 
.post .footer{
	background: none !important;
}
          
#content .post .date{
	background: none;
  	margin: 0;
  	width: 100px;
}

}
