css:
@-moz-document domain("fleaflicker.com") {

/*** style created by neyel8r on 2013.09.01 ***/

.tt-query {background: #111 !important; color: #777 !important}
.tt-suggestion, ul > li, .relative-date, .league-name a {color: #aaa !important}
body, th, td:not(.scoreboard), .container-fluid, .nfl, #top-bar {background: #222 !important}
h4:not(.list-group-item-heading):not(.media-heading), .container-fluid > table, .callouts > table td {background: #BCE8F1 !important}
.media-heading, .media-body > h4.media-heading, h4 > .player, .player > a, h4.list-group-item-heading, .fp {color: silver !important}
.form-actions, ul > li {background: transparent !important}
.well, dl.panel-body, .panel-body, .list-group-item, .panel-default, .list-group {background: #333 !important; border-color: #2a2a2a !important; color: #aaa !important}
.panel-heading, .panel-footer {color: silver !important; background: #3c3c3c !important; border-bottom: none !important}
.panel-heading {font-size: 16px !important}
.progress {background: #2a2a2a !important}
.dark {color: #fff !important}
.ad {display: none !important}
.disabled a {background: #555 !important}
.img-polaroid {border-color: #000 !important} 
h4:not(.list-group-item-heading), li {color: #000 !important}
th, td {border-color: #191919 !important}
td, label, .text-muted, .disabled, .pull-right > li a, .dropdown-menu > li a, .points-final, .player-text {color: #606060 !important}
.dropdown-menu > li a:hover {background: #333 !important}
.player-text {font-size: 14.4px; font-weight: normal !important}
.muted, p, .media-body-inner {color: #777 !important}
h1, dd, ol > li {color: #888 !important}
.active, .tt-content:not(.projected-starter):not(.text-red):not[class^="progress"] {color: #aaa !important}
input, select, #message {border-color: #333 !important}
input, .block:not(label), .tt-dropdown-menu, #message, .form-control, .dropdown-menu {background: #111 !important}
.right.text-right:not(.leaf), .label-success {color: #fff !important; font-weight: bold !important; font-size: 15px !important}
.warning, .right.alert {background-color: yellow !important; padding: 7px 0px 0px 10px !important; border-radius: 7px 7px 0px 0px !important}
dd.warning {margin-left: -4px !important; margin-right: -4px !important; margin-top: -15px !important; padding-left: 30px !important; padding-right: 20px !important; font-size: 40px !important; font-weight: bold !important; color: #000 !important; border-radius: 0px 0px 7px 7px !important}
dt.warning {margin-left: -4px; margin-right: -4px !important}
.well .warning, .strong, thead {font-weight: bold !important}
td.text-right, .sorted {font-weight: bold !important; font-size: 13px !important}
.text-success:not(.user-name), .text-danger, .projected:not(.stat-summary), a.breakdown.pct, .projected-starter {font-weight: bold !important; font-size: 14px !important}
small.text-muted {font-size: 12px !important}
.small {font-size: 13px !important}
.stat-summary {color: #5f5f5f; font-size: 11px !important}
em {color: #888 !important}
.news-text, .news-date {font-weight: normal !important; font-size: 13px !important}
.scoreboard > .left > .league-name, .scoreboard > .right.text-right > span, #draft-clock * {font-size: 48px !important}
.label-success {padding: 0em .25em 0em !important}
.player a, .text-right:not(.right) > .points-in-prog {color: #ccc !important}
.text-right:not(.right) > .points-in-prog, .points-final {font-size: 15px !important}
.pro-opp-matchup a:not(.projected-starter), .player-team {color: #999 !important}
.in-progress.alert-info {padding-right: 3px; padding-left: 3px}
.logo-large {padding-top: 50px}
.btn-default {color: #999; text-shadow: none ; background: #444; border-color: #333 !important}
.btn-default:hover {color: #ddd; background: #5f5f5f !important}
/*
.logo-large {margin-right: 0px}
.col-sm-3:not(.flip) {width: 200px; position: relative; float: left; margin-left: auto; margin-right: 0%}
.col-sm-3.flip {width: 200px; position: relative; float: right; margin-right: auto; margin-left: auto}
*/

}
