css:
@-moz-document domain("legacy.esvbible.org") {
@font-face {
  font-family: 'Old Standard TT';
  font-style: normal;
  font-weight: 400;
  src: local('Old Standard TT Regular'), local('OldStandardTT-Regular'), url(http://themes.googleusercontent.com/static/fonts/oldstandardtt/v4/n6RTCDcIPWSE8UNBa4k-DAjWwMcLosnYS7_elQHKBmA.woff) format('woff');
}
@font-face {
  font-family: 'Old Standard TT';
  font-style: normal;
  font-weight: 700;
  src: local('Old Standard TT Bold'), local('OldStandardTT-Bold'), url(http://themes.googleusercontent.com/static/fonts/oldstandardtt/v4/5Ywdce7XEbTSbxs__4X1_LHCWPYgV_Fgx41n4RSinLs.woff) format('woff');
}
@font-face {
  font-family: 'Old Standard TT';
  font-style: italic;
  font-weight: 400;
  src: local('Old Standard TT Italic'), local('OldStandardTT-Italic'), url(http://themes.googleusercontent.com/static/fonts/oldstandardtt/v4/QQT_AUSp4AV4dpJfIN7U5KCfO6YLYlwZkGaKgYrym0o.woff) format('woff');
}
@font-face {
  font-family: 'PT Sans';
  font-style: normal;
  font-weight: 400;
  src: local('PT Sans'), local('PTSans-Regular'), url(http://themes.googleusercontent.com/static/fonts/ptsans/v5/LKf8nhXsWg5ybwEGXk8UBQ.woff) format('woff');
}

/* Main passage */
.esv-text{font-size:147.72% !important;}

div.passage{
  font-family: "Old Standard TT", Georgia, Times, serif !important;
  font-size: 98% !important;
}

div.passage p{
  line-height: 1.6em !important;
}

/*
.notes {
  font-size:0.9em !important;
  line-height: 140% !important;
}
.note { font-size:1.1em !important; }
*/


div.passage{
  text-shadow: 0 0 1px #bababa !important;
}

.esv-text h2 {
  font-weight: normal !important;
  font-style: normal !important;
  text-transform: uppercase !important;
}

.esv-text h3 {
  font-size: 90% !important;
  font-style: normal !important;
  text-transform: uppercase !important;
}
  
.esv-text .footnote {
  font-size: 70% !important;
  text-shadow: 0 0 0 !important;
}


/* Sidebar */
div.sidebar_content {
  font-family: 'PT Sans', Arial, Helvetica, sans-serif !important;
  line-height: 1.6em !important;
  text-shadow: 0 0 1px #cbcbcb !important;
}
}
