@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url(http://myanimelist.net/),
    url-prefix(http://myanimelist.net/login),
    url-prefix("http://myanimelist.net/addtolist"),
    url-prefix("http://myanimelist.net/panel.php"),
    url-prefix("http://myanimelist.net/profile"),
    url-prefix("http://myanimelist.net//profile"),
    url-prefix("http://myanimelist.net/editprofile"),
    url-prefix("http://myanimelist.net/myfriends"),
    url-prefix("http://myanimelist.net/clubs"), 
    url-prefix("http://myanimelist.net/mymessages"),
    url-prefix("http://myanimelist.net/myreviews"),
    url-prefix("http://myanimelist.net/myrecommendations"),
    url-prefix("http://myanimelist.net/blog"),
    url-prefix("http://myanimelist.net/myblog"),
    url-prefix("http://myanimelist.net/anime.php"),
    url-prefix("http://myanimelist.net/manga.php"),
    url-prefix("http://myanimelist.net/people"),
    url-prefix("http://myanimelist.net/character"),
    url-prefix("http://myanimelist.net/fansub-groups"),
    url-prefix("http://myanimelist.net/topanime"),
    url-prefix("http://myanimelist.net/topmanga"),
    url-prefix("http://myanimelist.net/favorites"),
    url-prefix("http://myanimelist.net/reviews"),
    url-prefix("http://myanimelist.net/news"),
    url-prefix("http://myanimelist.net/forum/"), 
    url-prefix("http://myanimelist.net/users"),
    url-prefix("http://myanimelist.net/manga/"),
    url-prefix("http://myanimelist.net/anime/"),
    url-prefix("http://myanimelist.net/modules"),
    url-prefix("http://myanimelist.net/showreview"),
    url-prefix("http://myanimelist.net/about"),
    url-prefix("http://myanimelist.net/donations"),
    url-prefix("http://myanimelist.net/tou.php"),
    url-prefix("http://myanimelist.net/privacy"),
    url-prefix(http://myanimelist.net/dbchanges),
    url-prefix(http://myanimelist.net/comments),
    url-prefix(http://myanimelist.net/friends),
    url-prefix(http://myanimelist.net/showclubs),
    url-prefix(http://myanimelist.net/editclub),
    url-prefix(http://myanimelist.net/topusers),
    url-prefix(http://myanimelist.net/shared),
    url-prefix(http://myanimelist.net/history),
    url-prefix(http://myanimelist.net/recommendations),
    url-prefix(http://myanimelist.net/info.php),
    url-prefix(http://myanimelist.net/comtocom.php),
    url-prefix(http://myanimelist.net/register),
    url-prefix(http://myanimelist.net/admin),
    url-prefix(http://myanimelist.net/password),
    url-prefix("http://myanimelist.net/editlist.php"),
    url-prefix("http://myanimelist.net/ajaxtb.php"),
    url-prefix("http://myanimelist.net/import.php")
{
	body {
		background-color: #0c0c0b !important;
		background-image: none !important;
		color: #7E7E7E !important;
	}
	
	a {
		color: #cecece !important;
		text-decoration: none !important;
	}

	a:visited {
		color: #7E7E7E !important;
		text-decoration: none !important;
	}

	a:hover {
		color: #a7a7a7 !important;
		text-decoration: underline !important;
	}
	
	#headerSmall, #header {
		background: url("https://i.imgur.com/4nBEIDQ.jpg") no-repeat scroll 0% 0% transparent !important;
	}
	
	#menu, #menu_left, #menu_right, #nav a {
		background-color: #070808 !important;
		color: #7E7E7E !important;
	}
	
	#nav a:hover {
		background-color: #1e1f21 !important;
	}
	
	#roadblock {
		border-style: none !important;
	}
	
	h1 {
		background-color: #070808 !important;
		color: #7E7E7E !important;
		border-style: none !important;
	}
	
	h2 {
		color: #7E7E7E !important;
		border-color: #1e1f21 !important;
	}
	
	#horiznav_nav {
		border-color: #1e1f21 !important;
	}
	
	#horiznav_nav a, #horiznav_nav ul li{
		background-color: #070808 !important;
		color: #7E7E7E !important;
	}
	
	#horiznav_nav ul li .horiznav_active, #horiznav_nav ul li a:hover{
		background-color: #1e1f21 !important;
	}
	
	.borderClass {
		border-color: #1e1f21 !important;
	}
	
	td[style="background-color: #f6f6f6;"] {
		background-color: #1e1f21 !important;
		border-color: #070808 !important;
	}
	
	#profileRows a {
		color: #cecece !important;
		border-color: #1e1f21 !important;
	}
	
	#profileRows a:hover {
		background-color: #1e1f21 !important;
	}
	
	#rightStufButton .redButton {
		background-color: #9e0000 !important;
	}
	
	#rightStufButton .redButton a:hover {
		background-color: #ff0000 !important;
	}
	
	.bgColor1 {
		background-color: #070808 !important;
	}
	
	.bgColor2 {
		background-color: #0c0c0b !important;
	}
	
	.inputButton, .button_form, .Lightbox_AddEdit {
		background-color: #1e1f21 !important;
		border-color: #1e1f21 !important;
	}
	
	.forum_boardrow1, .forum_boardrow2 {
		background-color: #1e1f21 !important;
		border-color: #070808 !important;
	}
	
	
	#content {
		background-color: #070808 !important; 
		border-style: none !important;
	}
	
	#rightcontentunder {
		background-color: #070808 !important; 
		border-style: none !important;
		margin-top: 10px !important;
	}
	
	/* index */ 
	
	#header {
		height: auto !important;
	}
	
	#header a {
		height: 50px !important;
	}
	
	.container, #panel_right, #contentHome {
		background-color: #070808 !important;
		border-color: #1e1f21 !important;
	}
	
	#contentHome {
		border-width: 1px !important;
	}
	
	/* login */
	
	#fancybox-outer {
		background-image: none !important;
		background-color: #070808 !important;
	}
	
	/* profile */
	.friendHolder {
		background-color: #0c0c0b !important;
	}
	
	.profile_leftcell, .normal_header {
		border-color: #1e1f21 !important;
		color: #7E7E7E !important;
	}
	
	/* messages */
	
	.row_highlighted, .row_not_highlighted {
		background-color: #1e1f21 !important;
		border-color: #7E7E7E !important;
	}
	
	.inboxsent_header {
		background-color: #070808 !important;
		border-color: #1e1f21 !important;
	}
	
	#dialog {
		border-color: #1e1f21 !important;
	}
	
	#dialog td {
		border-color: #7E7E7E !important;
	}
	
	/* forums */
	
	.forum_category {
		background-color: #070808 !important; 
		color: #7E7E7E !important;
		padding: 10px !important;
	}
	
	.quotetext {
		background-color: #070808 !important; 
		border-color: #0c0c0b !important;
	}
	
	/* bar colors */
	div[style*="background-color:#eaf0fa"]  { background-color:#222222 !important; }
	div[style*="background-color:#dbe5f6"]  { background-color:#2E2E2E !important; }
	div[style*="background-color:#cddbf2"]  { background-color:#3A3A3A !important; }
	div[style*="background-color:#bdceec"]  { background-color:#464646 !important; }
	div[style*="background-color:#b0c4e8"]  { background-color:#535353 !important; }
	div[style*="background-color:#a3bae3"]  { background-color:#5F5F5F !important; }
	div[style*="background-color:#96b0de"]  { background-color:#6B6B6B !important; }
	div[style*="background-color:#8aa7d8"]  { background-color:#777777 !important; }
	div[style*="background-color:#7c9acf"]  { background-color:#848484 !important; }
	div[style*="background-color:#7191c8"]  { background-color:#909090 !important; }
	div[style*="background-color:#6687c0"]  { background-color:#9C9C9C !important; }
	div[style*="background-color:#5b7db8"]  { background-color:#A9A9A9 !important; }
	div[style*="background-color:#5073af"]  { background-color:#B5B5B5 !important; }
	div[style*="background-color:#476aa6"]  { background-color:#C1C1C1 !important; }
	div[style*="background-color:#3e619d"]  { background-color:#CDCDCD !important; }
	div[style*="background-color:#365a97"]  { background-color:#DADADA !important; }
	div[style*="background-color:#2f528f"]  { background-color:#E6E6E6 !important; }
	div[style*="background-color:#274a86"]  { background-color:#F2F2F2 !important; }
	
}
