css:
/* facebook - smarter emoticons - by pfeiffer stylez customizing */
/* http://userstyles.org/styles/93906/facebook-smarter-emoticons */

@-moz-document domain("facebook.com") {

.emoticon:not(.emoticon_like),
._1az._2g7, ._1az._2fx, ._1az._2gj, ._1az._2gd, ._1az._2g9,
._1az._2gn, ._1az._2fu, ._1az._2ft, ._1az._2g2, ._1az._2g8,
._1az._2g4, ._1az._2g6, ._1az._2h1, ._1az._2gf, ._1az._2gl,
._1az._2gm, ._1az._2ga { 
width: 17px !important; height: 16px !important;
background-image: url(https://dl.dropboxusercontent.com/s/rezxnam0obwlvkq/33a3or8.png) !important;
background-repeat: no-repeat !important;
}
.emoticon.emoticon_angel,
.emoticon.emoticon_devil { 
width: 17px !important; height: 19px !important;
}
._5907 .emoticon.emoticon_angel,
._5907 .emoticon.emoticon_devil { 
width: 17px !important; height: 18px !important;
}
.emoticon.emoticon_pacman {
background-image: url(https://dl.dropboxusercontent.com/s/r3oa70x4assy389/2wmhyqf.gif) !important;
background-size: 17px 16px !important;
}
.emoticon.emoticon_squint,
._1az._2g0, ._1az._2go {
background-image: url(https://dl.dropboxusercontent.com/s/0deraakz28wpbcg/21o2erq.gif) !important;
}
.emoticonsTable ._51m- {
padding: 0px 8px 10px 8px !important;
}
.emoticon_smile { background-position: 0px 0px !important; }
.emoticon_frown, ._1az._2g2, ._1az._2g8 { background-position: 0px -20px !important; }
.emoticon_tongue, ._1az._2g6, ._1az._2g7 { background-position: 0px -40px !important; }
.emoticon_grin, ._1az._2gd, ._1az._2fu, ._1az._2ft { background-position: 0px -60px !important; }
.emoticon_gasp, ._1az._2gn { background-position: 0px -80px !important; }
.emoticon_wink, ._1az._2fx { background-position: 0px -100px !important; }
.emoticon_pacman { background-position: 0px 1px !important; }
.emoticon_grumpy { background-position: 0px -120px !important; }
.emoticon_unsure { background-position: 0px -140px !important; }
.emoticon_cry, ._1az._2gj, ._1az._2go { background-position: 0px -160px !important; }
.emoticon_kiki { background-position: 0px -180px !important; }
.emoticon_glasses { background-position: 0px -200px !important; }
.emoticon_sunglasses { background-position: 0px -220px !important; }
.emoticon_heart, ._1az._2g4 { background-position: 0px -240px !important; }
.emoticon_devil { background-position: 0px -259px !important; }
.emoticon_angel { background-position: 0px -280px !important; }
.emoticon_squint, ._1az._2g0, ._1az._2go { background-position: 0px 1px !important; }
.emoticon_confused { background-position: 0px -300px !important; }
.emoticon_upset, ._1az._2g9 { background-position: 0px -320px !important; }
.emoticon_colonthree { background-position: 0px -339px !important; }
/* blushed */ ._1az._2h1 { background-position: 0px -360px !important; }
/* scared */ ._1az._2gf, ._1az._2gl { background-position: 0px -380px !important; }
/* KO */ ._1az._2gm { background-position: 0px -400px !important; }
/* angry red */ ._1az._2ga { background-position: 0px -420px !important; }

.UFICommentContent span,
.UFICommentContent .UFICommentActorName {
line-height: 16px !important;
vertical-align: bottom !important;
}
.UFICommentContent .emoticon:not(.emoticon_angel):not(.emoticon_devil) {
position: relative !important;
top: -1px !important;
}
}
