css:
@-moz-document domain('drawception.com') {

a {
    color: #77c0ff !important
}
.wrapper {
    background-color: #444 !important
}
#nav-drag {
    background-color: #353535 !important
}
.btn-default {
    background-color: #7f7f7f !important;
    border-bottom-color: #666 !important;
    border-left-color: #666 !important;
    border-right-color: #666 !important;
    border-top-color: #666 !important;
    color: #CCC !important
}
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open .dropdown-toggle.btn-default {
    background-color: #757575 !important;
    border-bottom-color: #565656 !important;
    border-left-color: #565656 !important;
    border-right-color: #565656 !important;
    border-top-color: #565656 !important;
    color: #DDD !important
}
.btn-success {
    background-color: #2e2e2e !important;
    border-bottom-color: #262626 !important;
    border-left-color: #262626 !important;
    border-right-color: #262626 !important;
    border-top-color: #262626 !important;
    color: #CCC !important
}
.btn-success:hover,
.btn-success:focus,
.btn-success:active,
.btn-success.active,
.open .dropdown-toggle.btn-success {
    background-color: #232323 !important;
    border-bottom-color: #1c1c1c !important;
    border-left-color: #1c1c1c !important;
    border-right-color: #1c1c1c !important;
    border-top-color: #1c1c1c !important;
    color: #DDD !important
}
.btn-primary {
    background-color: #212184 !important;
    border-bottom-color: #1a1a68 !important;
    border-left-color: #1a1a68 !important;
    border-right-color: #1a1a68 !important;
    border-top-color: #1a1a68 !important;
    color: #CCC !important
}
.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.open .dropdown-toggle.btn-primary {
    background-color: #191964 !important;
    border-bottom-color: #141450 !important;
    border-left-color: #141450 !important;
    border-right-color: #141450 !important;
    border-top-color: #141450 !important;
    color: #DDD !important
}
.btn-info {
    background-color: #2d8787 !important;
    border-bottom-color: #236969 !important;
    border-left-color: #236969 !important;
    border-right-color: #236969 !important;
    border-top-color: #236969 !important;
    color: #CCC !important
}
.btn-info:hover,
.btn-info:focus,
.btn-info:active,
.btn-info.active,
.open .dropdown-toggle.btn-info {
    background-color: #1c5454 !important;
    border-bottom-color: #133939 !important;
    border-left-color: #133939 !important;
    border-right-color: #133939 !important;
    border-top-color: #133939 !important;
    color: #DDD !important
}
.navbar-default .navbar-toggle:hover,
.navbar-default .navbar-toggle:focus {
    background-color: #3b3b3b !important
}
.navbar-toggle {
    background-color: #393939 !important
}
.navbar {
    border-bottom: 1px solid #000 !important
}
.forum-thread-starter,
.breadcrumb,
.regForm {
    background-color: #555 !important
}
.form-control {
    background-color: #555 !important;
    border: 1px solid #000 !important;
    color: #EEE !important
}
code,
pre {
    background-color: #656 !important;
    color: #FCC !important
}
body {
    color: #EEE !important
}
footer {
    background-color: #333 !important;
    border-top: 1px solid #000 !important
}
.pagination>li>a:hover,
.pagination>li>span:hover,
.pagination>li>a:focus,
.pagination>li>span:focus {
    background-color: #444 !important
}
.pagination>li>a,
.pagination>li>span {
    background-color: #555 !important;
    border: 1px solid #000 !important
}
.pagination>.active>a,
.pagination>.active>span,
.pagination>.active>a:hover,
.pagination>.active>span:hover,
.pagination>.active>a:focus,
.pagination>.active>span:focus {
    background-color: #666 !important;
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important
}
.drawingForm {
    background-color: #555 !important
}
.well {
    background-color: #666 !important;
    border: 1px solid #333 !important
}
#timeleft {
    color: #AAA !important
}
legend {
    border-bottom: 1px solid #000 !important
}
.thumbnail {
    background-color: #555 !important
}
.thumbpanel img {
    background-color: #fffdc9 !important
}
.panel-number,
.modal-content,
.profile-user-header {
    background-color: #555 !important
}
#commentForm {
    background-color: #555 !important;
    border: 1px solid #000 !important
}
.comment-holder,
.modal-header,
.nav-tabs {
    border-bottom: 1px solid #000 !important
}
hr,
.modal-footer {
    border-top: 1px solid #000 !important
}
.store-item {
    background: #666 !important;
    background-color: -moz-linear-gradient(top, #666 0, #333 100%) !important;
    background-color: -webkit-gradient(linear, left top, left bottom, color-stop(0, #666), color-stop(100%, #333)) !important;
    background-color: -webkit-linear-gradient(top, #666 0, #333 100%) !important;
    background-color: -o-linear-gradient(top, #666 0, #333 100%) !important;
    background-color: -ms-linear-gradient(top, #666 0, #333 100%) !important;
    background-color: linear-gradient(to bottom, #666 0, #333 100%) !important;
    border: 1px solid #222 !important
}
.store-item:hover {
    border: 1px solid #000 !important
}
.store-item-title {
    background-color: #222 !important;
    color: #DDD !important
}
.store-title-link {
    color: #DDD !important
}
.profile-award {
    background-color: #222 !important
}
.profile-award-unlocked {
    background-color: #888 !important
}
.progress-bar {
    color: #CCC !important;
    background-color: #214565 !important
}
.progress {
    background-color: #333 !important
}
.progress-striped .progress-bar {
    background-image: -webkit-gradient(linear, 0 100%, 100% 0, color-stop(.25, rgba(0, 0, 0, 0.15)), color-stop(.25, transparent), color-stop(.5, transparent), color-stop(.5, rgba(0, 0, 0, 0.15)), color-stop(.75, rgba(0, 0, 0, 0.15)), color-stop(.75, transparent), to(transparent)) !important;
    background-image: -webkit-linear-gradient(45deg, rgba(0, 0, 0, 0.15) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, 0.15) 50%, rgba(0, 0, 0, 0.15) 75%, transparent 75%, transparent) !important;
    background-image: -moz-linear-gradient(45deg, rgba(0, 0, 0, 0.15) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, 0.15) 50%, rgba(0, 0, 0, 0.15) 75%, transparent 75%, transparent) !important;
    background-image: linear-gradient(45deg, rgba(0, 0, 0, 0.15) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, 0.15) 50%, rgba(0, 0, 0, 0.15) 75%, transparent 75%, transparent) !important
}
.progress-bar-success {
    background-color: #363 !important
}
.progress-bar-info {
    background-color: #367 !important
}
.progress-bar-warning {
    background-color: #863 !important
}
.progress-bar-danger {
    background-color: #733 !important
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus {
    color: #DDD !important;
    background-color: #555 !important;
    border: 1px solid #222 !important
}
.nav>li>a:hover,
.nav>li>a:focus {
    background-color: #333 !important;
    border-bottom-color: #222 !important;
    border-left-color: #111 !important;
    border-right-color: #111 !important;
    border-top-color: #111 !important
}
.nav>li.disabled>a,
.nav>li.disabled>a:hover,
.nav>li.disabled>a:focus {
    color: #555 !important
}
.table-striped>tbody>tr:nth-child(2n+1)>td,
.table-striped>tbody>tr:nth-child(2n+1)>th {
    background-color: #333 !important
}
.table-hover>tbody>tr:hover>td,
.table-hover>tbody>tr:hover>th {
    background-color: #555 !important
}
.table thead>tr>th,
.table tbody>tr>th,
.table tfoot>tr>th,
.table thead>tr>td,
.table tbody>tr>td,
.table tfoot>tr>td {
    border-top: 1px solid #333 !important
}
.news-alert {
    background-color: #555 !important;
    border: 2px solid #444 !important
}
.btn-menu {
    background-color: #2e2e2e !important
}
.btn-menu:hover {
    background-color: #232323 !important
}
.btn-yellow {
    background-color: #8a874e !important
}
.btn-yellow:hover {
    background-color: #747034 !important
}
a.label {
    color: #fff !important
}
a.text-muted {
    color: #999 !important
}
a.wrong-order {
    color: #F99 !important
}
div.comment-holder:target {
    background-color: #454 !important
}
.popover {
    background-color: #777 !important
}
.popover-title {
    background-color: #666 !important;
    border-bottom: 1px solid #444 !important
}
.popover.top .arrow:after {
    border-top-color: #777 !important
}
.popover.right .arrow:after {
    border-right-color: #777 !important
}
.popover.bottom .arrow:after {
    border-bottom-color: #777 !important
}
.popover.left .arrow:after {
    border-left-color: #777 !important
}
.bg-lifesupport {
    background-color: #444 !important
}
.gsc-control-cse {
    background-color: #444 !important;
    border-color: #333 !important
}
.gsc-above-wrapper-area,
.gsc-result {
    border: none !important
}
.gs-snippet {
    color: #AAA !important
}
.gs-visibleUrl {
    color: #8A8 !important
}
a.gs-title b,
.gs-visibleUrl b {
    color: #EEE !important
}
.gsc-adBlock {
    display: none !important
}
#jappix_mini a {
    color: #000 !important
}
a:visited.thumbnail {
    border-color: #555 !important
}
a.anbt_replaypanel:hover {
    color: #8af !important
}

}
