css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.playhawken.com") {
    /* Fix ignored post colors */
    .post_ignore {
        color: inherit !important;
        background-color: #0D0E0E !important;
    }
    
    /* Fix unloaded color for fast-reply form */
    #ips_fastReplyForm {
        background-color: #000000 !important;
    }
    
    /* Fix item unfollow confirmation color */
    .ipsBox_container.ipsType_textblock {
        color: inherit !important;
    }
}
