css:
@-moz-document domain('nodictionaries.com') {#customized-box {
    background-color: #ffffff;
}

#display-options {
    display: none;
}

#lang-dropdown {
    display: none;
}

#login-status {
    display: none;
}

#native-vocab-slider {
    width: 17em;
}

#notes-description {
    display: none;
}

#notes-xbox {
    display: none;
}

#vocab-amount .lemma {
    background-color: white;
}

::-webkit-scrollbar {
    border-radius: 0px;
    height: auto;
    width: 10px;
}

::-webkit-scrollbar-track-piece {
    background-color: white;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

body {
}

body #bottom-internav {
    display: none ;
}

body #top-nav {
    display: none ;
}

body #top-nav a.co.current {
    background-color: #cccccc;
}

body #top-nav a:link {
    color: #666666 ;
}

body #top-nav a:visited {
    color: #666666 ;
}

body .date {
    color: #808080 ;
}

body .lemma {
    color: #666666;
    page-break-before: avoid ;
}

body .logo {
    color: #000000 ;
}

body .logo > a:link {
    color: #000000 ;
}

body .logo > a:visited {
    color: #000000 ;
}

body .noticeable {
    display: none ;
}

body .nqlink {
    display: none ;
}

body .passage-title {
    visibility: hidden ;
}

body .subpassage-line {
    page-break-inside: avoid ;
}

body a:link {
    color: #000000;
    text-decoration: none ;
}

body div.maindiv {
    background-color: #FFFFFF;
    color: #000000 ;
}

body span.word {
    color: #000000;
    text-decoration: none ;
}

body.clean-page #display-options {
    display: inline;
}

body.clean-page .subpassage-line-text a:link {
    color: #000000 ;
}

body.clean-page .vocab-bucket {
    page-break-before: always ;
}

body.yui-skin-sam {
    background-color: #ffffff;
}

button.unprintworthy {
    display: none;
}

div.maindiv {
    font-family: Palatino;
}

form div input {
    -moz-border-radius: 3px;
    -moz-box-shadow: inset 0 0 1px 1px #f6f6f6;
    -webkit-border-radius: 3px;
    -webkit-box-shadow: inset 0 0 1px 1px #f6f6f6;
    background: #e3e3e3;
    border: 1px solid #bbb;
    border-radius: 0px;
    box-shadow: inset 0 0 1px 1px #f6f6f6;
    color: #333;
    font: bold 12px "helvetica neue", helvetica, arial, sans-serif;
    height: 30px
;
    line-height: 1;
    margin-bottom: 30px;
    text-align: center;
    text-shadow: 0 1px 0 #fff;
    width: 200px;
}

form div input:active {
    -moz-box-shadow: inset 0 0 1px 1px #e3e3e3;
    -webkit-box-shadow: inset 0 0 1px 1px #e3e3e3;
    background: #d0d0d0;
    box-shadow: inset 0 0 1px 1px #e3e3e3;
    color: #000;
}

form div input:hover {
    -moz-box-shadow: inset 0 0 1px 1px #eaeaea;
    -webkit-box-shadow: inset 0 0 1px 1px #eaeaea;
    background: #d9d9d9;
    box-shadow: inset 0 0 1px 1px #eaeaea;
    color: #222;
    cursor: pointer;
}

form div textarea {
    background-color: #fafafa;
    border-color: #ffffff;
    box-shadow: 0 0 10px #585858;
    color: #171717;
    font-family: Palatino;
    font-size: 17px;
    min-height: 200px;
    min-width: 60%;
    outline: none;
}

h1.logo {
    font-family: Georgia;
    font-size: 30px;
    font-variant: small-caps;
    font-weight: normal;
}

html {
    background-color: #ffffff;
}

span.interlinear-note-add-text a {
    display: none;
}

span.unprintworthy {
    display: none;
}

table.interlinear-note-add.lh0.lemma.tw17839768 {
    display: none;
}

#customize:hover {
    top: 10px;
    background-image: none;
    

}

#customize {
    background-color: #ffffff;
    box-shadow: 0 0 10px #585858;
    font-family: Palatino;
    font-variant: small-caps;
    height: auto;
    padding-bottom: 0;
    padding-left: 8px;
    padding-right: 7px;
    padding-top: 06px;
    position: fixed;
    right: 10px;
    top: -125px;
    width: auto;
    background-image: url('http://i44.tinypic.com/ezfu2o.jpg');
    background-repeat: no-repeat;
    background-position: center bottom;
    background-size: 30px 20px;
    transition: top 0.5s ease;
    
}

}
