	.nav .item{
		font-size:14px;
		padding-bottom:10px;
		padding-top:10px
	}

	#nav-main{
		background:#303030;
		font-size:13px;
		-webkit-border-radius:4px;
		-moz-border-radius:4px;
		border-radius:4px;
		-moz-background-clip:padding;
		-webkit-background-clip:padding-box;
		background-clip:padding-box
	}

	#nav-main li{
		width:auto
	}

	#nav-main ul{
		padding:0
	}

	#nav-main li:first-child{
