#main-window[stylish-url="about:Home"] #page-proxy-favicon,

#main-window[stylish-url="about:home"] #page-proxy-favicon,

.tabbrowser-tab[label="Mozilla Firefox Start Page"] .tab-icon-image,

#main-window[stylish-url="about:home"] #fav-image 

