css:
@-moz-document domain("u.to") {
*, textarea, input[type="text"], input[type="password"], #abuse_url{
  font-family: "Vrinda", "Kartika", monospace, sans-serif;
  scrollbar-base-color: #5093F3 !important;
  scrollbar-face-color: #5093F3 !important;
  scrollbar-track-color: #3093F3 !important;
}
body, #mainDiv, table.middleLink, #contentBlock, #bottomBlock, #bottomBody, td, tr,
 #mainDiv div table tbody tr td div, .xw-tl, .xw-tr, .xw-tc, .xw-sps, .xw-hdr, .xw-draggable, .xw-ml,
 .xw-mr, .xw-mc, .xw-body, .xw-bl, .xw-br, .xw-bc, legend{
  color: gray !important;
  background: none !important;
  background-color: black !important;
}
div>b{
  color: green !important;
  background-color: black !important;
}
#bottomBlock, #contentBlock{
  border: none !important;
}
.shBotR, .shBotL{
  display: none !important;
}
div, table, table.middleLink{
  border-width: 2px !important;
  border-color: green !important;
}
#contentBlock, #bottomBody, .urls li>table>tbody>tr:first-child>td:first-child,
 .urls li>table>tbody>tr:first-child>td:last-child{
  border-top: 2px solid green !important;
  border-left: 2px solid green !important;
  border-right: 2px solid green !important;
  border-bottom: 2px solid green !important;
}
a, .topText, .xw-hdr-text, legend+input+div{
  color: green !important;
}
a#shurlbtn>span{
  color: black !important;
  font-weight: bold !important;
  background-color: #202020 !important;
}
#shurlin, #shurlout, #abuse_url, input{
  color: green !important;
  background: none !important;
  border: 2px solid green !important;
  background-color: #202020 !important;
}
.shRight, .shLeft{
  background: none !important;
}
a, a:Link, a:Visited{
  text-decoration: underline !important;
}
a:Hover{
  text-decoration: none !important;
}
#mainDiv div table tbody tr td div a img{
 content: url(http://imageshack.us/a/img819/9623/84c8.png) !important;
}
a.shurlbutton{
  background: transparent url(http://imageshack.us/a/img208/8945/ru0q.png) no-repeat scroll top right !important;
}
a.shurlbutton span{
  background: transparent url(http://imageshack.us/a/img208/8945/ru0q.png) no-repeat !important;
}
a.shurlbutton:link,a.shurlbutton:visited {
  text-decoration: none !important;
}
a.shurlbutton:hover, a.shurlbutton:hover span {
  background-image: url(http://imageshack.us/a/img20/2961/jdor.png) !important;
  text-decoration: none !important;
}
a.delete{
  background: transparent url(http://imageshack.us/a/img849/9070/bqk.gif) no-repeat scroll 0 2px !important;
}
a.delete:hover{
  background-position: 0 -18px !important;
}
a.edit{
  background: transparent url(http://imageshack.us/a/img18/2815/aadz.gif) no-repeat scroll 0 2px !important;
}
.tags a.tagedit{
  background: transparent url(http://imageshack.us/a/img18/2815/aadz.gif) no-repeat scroll 3px 3px !important;
}
a.edit:hover{
  background-position: 0 -18px !important;
}
.tags a.tagedit:hover{
  background-color: black !important;
  text-decoration: none !important;
  background-position: 3px -18px !important;
}
div+input+i>img:first-child, span+input+i>img:first-child{
  content: url(http://imageshack.us/a/img202/8725/50q7.gif) !important;
}
div+input+i>img:last-child, span+input+i>img:last-child{
  content: url(http://imageshack.us/a/img42/7006/7vp.gif) !important;
}
.xt{
  background: transparent url(http://imageshack.us/a/img809/7140/9im5.gif) no-repeat scroll 0 0 !important;
}
.tags a{
  color: green !important;
  text-decoration: none !important;
  background: #202020 url(http://imageshack.us/a/img94/5061/m8y.gif) no-repeat scroll 0 -1px !important;
}
.tags a:hover{
  text-decoration: underline !important;
  background-color: gray !important;
}
.urls li>table>tbody>tr:first-child>td:first-child, .urls li>table>tbody>tr:first-child>td:last-child, h2>a.h2-old,
 div>h2, .stat_table, .stat_table th{
  background: none !important;
  background-color: #202020 !important;
}
.shurlbutton{
  background-color: #202020 !important;
}
span#form_cpu{
  color: gray !important;
  background-color: #202020 !important;
}
span.action b{
  font-weight: bold !important;
}
.myWinLoad{
  background: transparent url(http://imageshack.us/a/img833/6431/m1kn.gif) no-repeat 0 0 !important;
}
.myWinLoadS{
  background:transparent url(http://imageshack.us/a/img163/3039/kszs.gif) no-repeat 0 0 !important;
}
input[type="checkbox"]{
  width:  13px !important;
  height: 13px !important;
  border: none !important;
  -moz-appearance:none !important;
  -webkit-appearance:none !important;
  -o-appearance:none !important;  
  background: transparent url(http://imageshack.us/a/img138/8159/xtvl.png) no-repeat !important;
} 
input[type="checkbox"]:checked{
  background-position: 0 -14px !important;
}
.pgSwchA{
  border: none !important;
  background-color: black !important;
  color: gray !important;
}
a.pgSwch:link, a.pgSwch:visited, a.pgSwchB:link, a.pgSwchB:visited,
 a.pgSwchN:link, a.pgSwchN:visited{
  text-decoration: none !important;
  border: 1px solid gray !important;
  color: green !important;
}
a.pgSwch:hover, a.pgSwchB:hover, a.pgSwchN:hover{
  text-decoration: none !important;
  background-color: #202020 !important;
  color: green !important;
  border: 1px solid gray !important;
}
a.statistic, .reallink a, a.shu, a.h2-old{
  text-decoration: none !important;
}
a.statistic:hover, .reallink a:hover, a.shu:hover, a.h2-old:hover{
  text-decoration: underline !important;
}
::selection{
  background: green !important;
  color: gray !important;
}
::-moz-selection{
  background: green !important;
  color: gray !important;
}

}
