css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com") {
form#search_form{padding-left: 0px !important}
.tod {display: none;}
a#main_link p img[src='/graphics/startp_logo_adv.gif'], a#main_link p img[src='/graphics/startp_logo.gif']{height: 0 !important;width: 0 !important;padding-left: 218px !important;padding-top: 113px !important;background: url("data:image/png;base64,") no-repeat !important;}
input#submit{height: 0 !important;width: 0 !important;padding-left:0px !important;padding-top: 0px !important;background-color: #4D90FE !important;}
div#submit_bg{height: 0 !important;width: 0 !important;padding-left:0px !important;padding-top: 0px !important;background-color: #4D90FE !important;border-radius:0pt 4px 4px 0pt}
input#query, input#submit{border-radius: 0 0 0 0 !important; border-color: #D9D9D9 !important}
input#query{width:555px; height:18px!important;font-size:15px!important}
input#query:focus, input#query_top:focus ,input#query_bottom:focus{box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3) inset !important; border-color:#4D90FE !important}
input#query:hover:focus, input#query_top:hover:focus ,input#query_bottom:hover:focus{box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3) inset !important; border-color:#4D90FE !important}
input#query:hover, input#query_top:hover ,input#query_bottom:hover{border-color:#B9B9B9 !important}
html,body{background:none!important}
div#head{background:#ffffff;margin:0!important}
div#head table[cellspacing="1"][cellpadding="1"], a#main_link p#caption, div#enhanced, div#links{display:none}
div#head table[width="100%"], div#head{padding:0 12px;color:#ffffff;height:30px;border:none;border-spacing:0;border-bottom:1px solid #E5E5E5}
div#head table[width="100%"] td{padding:0}
div#head table[width="100%"] a, div#head a{color:#337fe2!important;display:inline-block;height:23px;padding:7px 4px 0 4px;margin:0 -6px;font-size:13px!important;text-decoration:none!important}
div#head table[width="100%"] a.active, div#head a.active{color:#337fe2!important;border-top:2px solid #DD4B39;padding-top:5px}
div#head table[width="100%"] a:hover, div#head a:hover{background:#ffffff}
div#results_header{background:#F5F5F5;border-bottom:1px solid #E5E5E5;padding-top:10px;height:60px;width:100%!important;max-width:100%!important;margin:0!important}
div#logo{margin-top:37px!important}
div#logo a{width:110px;height:60px;display:block;background-image:url("data:image/png;base64,")}
form#search_form{max-width:950px}
td#input_p{text-align:left; width:auto !important}
input#query_top,input#query_bottom{width:496px!important;height:18px!important;margin-right:16px;border-color: #ccc!important;border-radius:0!important;box-shadow:-1px 1px 1px 0 #eee!important;font-size:15px!important}
input#query_top{margin-left:30px}
input#submit1{opacity:.0;height:29px!important;width:72px!important;margin-left:0!important}
td#input_p+td, input#submit, div#submit_bg{background-repeat:no-repeat;background-position:center center;background-image:url("data:image/png;base64,") !important}
td#input_p+td:hover, input#submit:hover, div#submit_box{background-image:url("data:image/png;base64,") !important}
p#results_count_p{padding:15px 0 15px 220px;margin:0 0 10px 0!important;font-size:13px!important;color:#999999;width:auto!important;border-bottom:1px solid #DEDEDE}
p#results_count_p span:before{content:"Search";visibility:visible!important}
p#results_count_p span{position:absolute;top:111px;left:30px;color:#DD4B39;font-size:20px;visibility:hidden}
div#side_bar{top:150px!important}
ul#by_time li a{color:#222222!important;text-decoration:none!important;font-size:13px}
ul#by_time li a:hover{text-decoration:underline!important}
ul#by_time li a.active{color:#DD4B39!important;}
ul#by_time li a.active:hover{text-decoration:none!important}
a.sugg{color:#1122CC!important;font-style:italic;text-decoration:underline!important;font-size:16px!important;padding-bottom:24px;display:inline-block}
div#results,div#tod_wrap,ul#by_time{border:none!important}
div#results{margin-top:-20px!important}
div.result{max-width:39em;text-align:justify;-moz-hyphens:auto;margin:0.45em 0 0.45em 0;position:relative}
div.result h3 a{color:#1122CC!important}
div.result span.url{color:#009933!important}
a.proxy{color:#A9A9F5!important}
a#proxy_link{overflow-x:hidden;display:inline-block;width:34px;height:16px;vertical-align:bottom;text-align:left;-moz-hyphens:none;}
a#proxy_link:before{content:"Proxy "}
div#enhanced,a#detail,a.bookmark{visibility:hidden}
div#logo img,div#sponsored,div#show_hide,div.date_promo,div[style="padding-bottom:24px;"],
a.tod[href$="/protect-privacy.html"]{display:none!important}
#footer{display:none!important}
body>#footer{display:block!important}
/* alternatively: */ /* #results_content ~ #footer{display:block!important} */
#search_form .midline_right { display: none !important; }
#search_form .midline { margin: 0 auto !important; }
}
