css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
.fbIndex .loggedout_menubar_container
{
    background-color: black !important;
}

.fbIndex #u_0_c, .fbIndex #pageFooter, .fbIndex #globalContainer 
{
    background-color: black !important;
}

.fbIndex #globalContainer
{
    padding-bottom: 10px;
}

.fbIndex .uiButtonConfirm
{
    background-position: 62px 100px !important;
    background-color: black !important;
}

.fbIndex label, .fbIndex a
{
    color: white !important;
}

.fbIndex button
{
    background: -moz-linear-gradient(center top , black, black) repeat scroll 0% 0% black !important;
    box-shadow: 0px 0px 0px 0px !important;
    border-color: black !important;
}

.fbIndex div
{
    color: black !important;
}

.fbIndex .pam
{
    background-color: black;
}

.fbIndex .mlm._58ms, .fbIndex ._58mt
{
    color: grey !important;
}
}
