css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jisko.net") {

body {
color: #E6E6E6 !important;
}

a {
color: #7CB1EE !important;
}

.menu {
font-size: 10px !important;
background: transparent url('http://img231.imageshack.us/img231/5401/webbgje7.png') repeat scroll 0 0 !important;
height: 22px !important;
padding: 7px 15px 0 !important;
width: auto !important;
}

.web {
background: transparent url('http://img231.imageshack.us/img231/5401/webbgje7.png') repeat scroll 0 0 !important;
}

.content {
background: transparent url('http://img231.imageshack.us/img231/5401/webbgje7.png') repeat scroll 0 0 !important;
}

.content_top {
background: transparent url('http://img244.imageshack.us/img244/6867/webbg2tr3.png') repeat scroll 0 0 !important;
}

.footer {
background: transparent url('http://img231.imageshack.us/img231/5401/webbgje7.png') repeat scroll 0 0 !important;
}

.ok {
background: transparent url('http://img185.imageshack.us/img185/6743/notesbgur9.png') repeat scroll 0 0 !important;
color: #CCCCCC !important;
font-weight: bold !important;
width: auto !important;
height: auto !important;
border: 1px dotted #B1DE1B !important;
margin-bottom: 30px !important;
}

.user_note a {
color: #E6E6E6 !important;
}

.content_right_top {
color: #E6E6E6 !important;
}

.note {
background: transparent url('http://img185.imageshack.us/img185/6743/notesbgur9.png') repeat scroll 0 0 !important;
color: #E6E6E6 !important;
border: none !important;
border-top: 1px dotted #B1DE1B !important;
border-bottom: 1px dotted #B1DE1B !important;
}

.info_note {
border-bottom: 1px solid #B1DE1B !important;
}

.note_t {
background: transparent url('http://img515.imageshack.us/img515/493/notestbgto4.png') repeat scroll 0 0 !important;
color: #E6E6E6 !important;
border: none !important;
border-top: 1px dotted #3383A3 !important;
border-bottom: 1px dotted #3383A3 !important;
}

.note_t .info_note {
border-bottom: 1px solid #3A93B6 !important;
}

.date_note {
color: #CCCCCC !important;
}

#tooltip-text {
background: black !important;
}

.pagination a {
background: black !important;
}

.pagination span.disabled {
background: black !important;
}

.info_title {
color: #CCCCCC !important;
}

.counter {
color: #789BA1 !important;
}

ul.tags li {
background: black !important;
}

ul.tags li a:hover {
background: #333333 !important;
}

ul.tags li.current_item {
background: #333333 !important;
}

.submit {
background: black !important;
color: #CCCCCC !important;
}

.submit:hover {
background: #333333 !important;
color: #CCCCCC !important;
}

.submit_follow {
background: black !important;
color: #CCCCCC !important;
}

.submit_follow:hover {
background: #333333 !important;
color: #CCCCCC !important;
}

.input {
background: #FAFAFA none repeat scroll 0 0 !important;
}
}
