css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
#profile_full_info h4 b, #profile_full_info h4 a.fl_r, #profile_full_info h4 a.fl_l {
		color: black !important
		}
	.profile_no_info {
		display: none !important
		}
	#profile_info .profile_info {
		padding-bottom: 0px !important
		}
	/* Header line */
	#header {
		background: #E1E7ED !important; 
		border: 1px solid #E1E7ED !important
		}

#page_header, #pageHeader {
		background: url(http://imgs.su/tmp/2013-09-19/1379605513-500.jpg) !important
		}
	#top_logo_down {
	    background: url(http://imgs.su/tmp/2013-09-19/1379605513-500.jpg) !important
		}
	#page_header .back, #page_header .right, #page_header .left, #pageHeader .header_back,
	#pageHeader .header_right, #pageHeader .header_left {
		display:none !important
		}

body {background: #ffffff url(http://imgs.su/tmp/2013-09-19/1379606122-500.jpg) no-repeat fixed left center !important}

#top_back_link {background: url(http://bosnyakvn.at.ua/vk/backlink1.gif) no-repeat left -85px !important;}
#top_back_link:hover {background: url(http://bosnyakvn.at.ua/vk/backlink2.png) no-repeat left -85px !important; background-color: rgba(0, 0, 0, 0.1) !important;}
#head_play_btn {background: url('http://bosnyakvn.at.ua/vkm.png') no-repeat scroll -24px 0 transparent !important;}
a:hover #head_play_btn.playing {background-position: -24px -115px !important;}
a:hover #head_play_btn {background-position: -24px -23px !important;}
#head_play_btn.playing {background-position: -24px -92px !important;}
#header {
background: #f0efce !important;
color: #853636 !important;}
#header span, #header b {color: #c2ab00 !important;}
h4 {color: #8c3a3a !important;}
a {color: #78712b !important;}
.left_box.attention h4 {color: #8c3a3a !important;}
.left_row:hover {background: #eaebd5 !important;}
.module_header .header_top {
background: #ebdfb9 !important;
color: #8c3f3f !important;}
.module_header .header_top .right_link {color: #c2bb6d !important;}
ul.t0 li a:hover {color: #782b2b !important;}
ul.t0 li.active_link a, ul.t0 li.active_link a:hover {background: #a13333 !important; color: #FFFFFF !important;}
ul.t0 li.active_link a b.tl2, ul.t0 li.active_link a:hover b.tl2 {background-color: #a13333 !important;}
ul.t0 li.active_link a b.tl1 b, ul.t0 li.active_link a:hover b.tl1 b {background: #a13333 !important;}
ul.t0 li a:hover {background: #ebe3cc !important;}
ul.t0 li a:hover b.tl1, ul.t0 li a:hover b.tl2 {background-color: #ebe3cc !important;}
.summary_wrap {color: #782b2b !important;}
.summary_tab_sel .summary_tab2, .summary_tab_sel .summary_tab2:hover {background: #a13333 !important;}
.summary_tab2:hover {background: #ebe3cc !important;}
.summary_tab3 {color: #a13333 !important;}
.t_bar {border-bottom: solid 1px #a13333 !important;}
.summary_tab_sel .summary_tab3 {color: #FFF !important;}
.group_wiki_hider:hover {background: #f0e7d3 !important;}
#content .wall_module .post_like_wrap i, #content .wall_module .post_like_icon {background: url(http://bosnyakvn.at.ua/vk/rvlike2.png) no-repeat 1px 0px !important;}
.wall_module .post_share_icon {background: url(http://bosnyakvn.at.ua/vk/rvrep.png) no-repeat 1px -21px !important;}
.wall_module .post_like:hover,
.wall_module .post_share:hover {background: #f0ead5 !important;}
.post_like_link, .post_share_link {color: #ad3e3e !important;}
.wall_module .post_like_count, .wall_module .post_share_count {
font-weight: bold !important;
color: #b00000 !important;}
.wall_post_over .post_like_link,
.wall_module .post:hover .post_like_link,
.wall_post_over .post_share_link,
.wall_module .post:hover .post_share_link {
  color: #780c0c !important;}
#stl_text {
color: #8c4546 !important;
background: url(http://bosnyakvn.at.ua/vk/toplink1.png) no-repeat left 3px !important;}
.stl_active.over_fast #stl_bg {background-color: #ebe3ca !important;}
#stl_left .down {background-position: left -7px !important;}
.replies_open {
  color: #ad3e3e !important;
  background: #f0ecce !important;}
.replies_open:hover {background: #f0eddf !important;}
.wr_header {
color: #ad3e3e !important;
background: #f0ecce !important;}
.feed_tab_add_icon {background: url(http://bosnyakvn.at.ua/vk/ninp.png) no-repeat 0 -144px !important;}
#feed_tab_add_menu .feed_tab_add_icon {background-position: 0 -128px !important;}
.replies_side_icon {background: url(http://bosnyakvn.at.ua/vk/toplink1.png) 0 -58px no-repeat !important;}
.replies_side_over {background: #f0ecce !important;}
.wall_module .published_by {background: url(http://bosnyakvn.at.ua/vk/published.png) no-repeat 0px 3px !important;}
.wall_module .published_by_quote {border-left: 2px solid #deddc3 !important;}
.wall_signed_by {background: url(http://bosnyakvn.at.ua/vk/mono_iconset.png) left 1px no-repeat !important;}
.wall_module .media_preview .share b, .wall_module .media_preview .note b, .wall_module .media_preview .poll b, .wall_module .media_desc b {
background: url(http://bosnyakvn.at.ua/vk/mono_iconset.png) no-repeat left -237px !important;}
.wall_module .media_desc .video {background-position: 0px -75px !important;}
.wall_module .media_desc .a {color: #940606 !important;}
.wall_module .post_delete_button, .wall_module .reply_delete_button {
background: url(http://bosnyakvn.at.ua/vk/statusx_op.png) 0 0 no-repeat !important;}
.fmenu_icon {background: url(http://bosnyakvn.at.ua/vk/fixedmenu.png) no-repeat !important;}
#fmenu_fr {
  background-position: 100% -6px !important;}
#fmenu_ph {
  background-position: 100% -29px !important;}
#fmenu_vid {
  background-position: 100% -52px !important;}
#fmenu_msg {
  background-position: 100% -75px !important;}
#fmenu_gr {
  background-position: 100% -98px !important;}
#fmenu_nws {
  background-position: 100% -121px !important;}
#fmenu_ap {
  background-position: 100% -144px !important;}
.wall_module .bp_post .like_link, .wall_module .reply_info .like_link, .wall_module .pv_comment .like_link, .wall_module .mv_comment .like_link {color: #ad3e3e !important;}
#content .wall_module .like_wrap i {background: url(http://bosnyakvn.at.ua/vk/rvlike2.png) no-repeat 1px 0px !important;}
.wall_module .like_count, .fw_reply .like_count {color: #b00000 !important;}
.page_media_poll_title {color: #b00000 !important;}
.page_media_poll .page_poll_row_count {color: #ad3e3e !important;}
.page_media_poll .page_poll_percent {background: #f0ecce !important;}
.wall_module .reply_edit_button, .wall_module .post_edit_button {background-image: url(http://bosnyakvn.at.ua/vk/statusedit.png) !important;}
.wall_module .page_media_thumbed_link {border-left: 2px solid #deddc3 !important;}

}
