css:
@-moz-document domain("sd.offcn.com") {
.showzx_right,
.showzx_bot,
.copyright,
.showzx_share,
table[height="120"],
.showzx_upd,
.showzx_xglink,
.showzx_bianji,
.contentleft5
  {display: none !important;}

.sydw_contenttop h1 { 
  font-family: Arial,方正宋黑_GBK,黑体 !important;
}
.showzx_cont p {
  color: #333333;
  font-size: 24px !important;
  font-family: Arial,文泉驿微米黑 !important;
  line-height: 1.5em !important;
}
.showzx_cont p strong {
  color: #660000;
  font-size: 24px !important;
  font-family: Arial,方正大标宋_GBK,楷体,文泉驿微米黑 !important;
  line-height: 1.5em !important;

}
.showzx { background: none !important; }
.showzx_left,.showzx_cont,
.list_nav,
.firstlist_bt,.firstlist_lb 
  { width: 95% !important; }

.firstlist_lb  ul li:nth-child(2n) { background-color: #f2f2f2; }
}

@-moz-document domain("sydw.offcn.com") {
.kszx_left_tit,.kszx_left_tit_5,/*列表页提示*/
.kszx_right,
.kszx_fenpage,
.sydw_contentbottom,
.content_jrsc,
.qqqun,
.sydwtop,.sydwlogo,.sydwnav,.sydwfenxiao,.kszx_site img,.copyright

{ display: none !important;}

body { background-color: #212121 !important;}
.kszx { background-color: #101010 !important;  min-height: 1000px !important;}

/*列表页*/
.kszx_left_beikao { 
  width: 860px !important; 
  border: none !important;
}
.kszx_left_beikao *{ font-size: 16px !important;font-family: Arial,宋体 !important;}
.kszx_left_list { padding-top:0 !important;}
.kszx_left_line { width: 860px !important; }
.kszx_page { border: none !important; }
.kszx_page a { background: none !important;}

/*正文页*/
.sydw_content { width: 100% !important; }
.sydw_contenttop { width: 96% !important; border: none !important;}
.kszx_site,.kszx_site a{ color: #DFE0DF !important;}
.sydw_contenttop h1 { 
  font-family: Arial,方正宋黑_GBK,黑体 !important;
  color: #f5f5f5 ;
}

.sydw_contenttop .sydwneirong * {
  color: #cccccc !important;
  font-size: 26px !important;
  font-family: Arial,方正兰亭黑_GBK,文泉驿微米黑 !important;
  line-height: 1.5em !important;
  text-indent:2em !important;
}

.kszx_page a {
    background: none !important;
    border: none !important;
    color: #DFE0DF !important;
  }
}
