css:
@-moz-document domain(habrahabr.ru) {


body {
    background: #333/*#e6e6e6*/ !important;
    font-size: medium !important;
  	color: #bcbcbc !important;
}
#topline {
    height: 0 !important;
}

div#header a.logo,
div.header a.logo{
    display:none !important;
}

div#header div.search {
   position: static !important;
   float: right;
}
div.content_left table.menu * {
    background: #333/*#f4f4f4*/ !important;
    border: 0 !important;
}
div.content_left table.menu {
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
}
div.content_left div.submenu {
    background: #333/*#f4f4f4*/ !important;
    border: 1px solid #999 !important;
    border-top: 0 !important;
    border-radius: 0 !important;
    margin-top: -1px !important;
    margin-bottom: 10px !important;
}
div.post {
    background: #444/*#f4f4f4*/ !important;
    border: 1px solid #999 !important;
    padding: 0.25em 1em !important;
    margin-bottom: 10px !important;
}
h1.title {
    color: #bcbcbc/*#f4f4f4*/ !important;
}
a.post_title:hover {
  color: #dcdcdc !important;
}
a.post_title:visited:hover {
  color: #dcdcdc !important;
}
a.post_title {
  color: #cccccc !important;
}
div.content_left {
    width: auto !important;
    margin-right: 250px !important;
}
div.sidebar_right { 
    width: 230px !important;
    position: absolute !important;
    right: 10px !important;
}
.banner_240x400 {
    margin: 0 !important;
}
div.sidebar_right div.block {
    background: #333/*#f4f4f4*/ !important;
    border: 1px solid #999 !important;
    border-radius: 0 !important;
    margin-bottom: 10px !important;
}
div.comments_list {
    margin-top: 5px !important;
}
div.comments_list .comment .info {
   padding: 5px !important;
   background: #f4f4f4 !important;
   border: 1px solid #999 !important;
   border-bottom: 0 !important;
}
div.comments_list .comment .message {
   background: #f4f4f4 !important;
   border: 1px solid #999 !important;
   border-top: 0 !important;
   padding: 5px !important;
}
div.comments_list .comment .reply_comments {
   margin-top: 10px !important;
}
div.comments_list .comment .info .folding-dot {
   background: #999 !important;
   height: 1px !important;
}
div.comments_list .comment .info .folding-dot:after {
   content: "#";
}
a:visited:hover {
  color: #747 !important;
}
a:visited {
  color: #969 !important;
}
.poll_title {
  color: #CECECE !important;
}
.message.bad1 /*.bad4 .bad5*/
{
   background-color: #333 !important;
   color: #444 !important;
}


}
