css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://feedly.com"),
url-prefix("http://feedly.com") {
#frameActionsTop,#feedlyMessageBar,#sideArea,#section0,
#loadingEntries > img, 
#feedlyTitleBar > .hhint,
div[style='height:17px'], /*div spacer under x more sources*/
a[href*='plus\.google'],
div[data-uri^='pro'],
[class^='entry'] > br,
.nbrRecommendations,.u0Entry:hover .lastModified,.bottomWikiWidget,
a.websiteCallForAction,.feedIndexTitleHolder img.favicon,.sideAreaModule {
display: none !important;
}

.topWikiWidget{
display: inline-block!important;
width: 220px!important;
float: right !important;
}

/*------------------SPACING AND POSITIONING------------------*/
#feedlyTabsHolder.picturePicture .panels,
#floatingBar,
#feedlyDialogFramePart,
#herculePanel .herculeResult,
.popup,
body{
line-height: 118%!important;
background-color: #222 !important;
}

.condensedTools > a{
border-radius: 4px !important;
background-color: #fff !important;
}

.sourceInfo,
.feedIndexTitleHolder,
div[class*='UnreadCount'],
div[id^='feedly'],
[class$='Entry'] *,
*[style*='line-height'],
#floatingBar,
#floatingBar *{
line-height: inherit !important;
}

.hAction{
padding: 4px 12px 3px!important;
}

#feedlyTabs,#fixedProfile,
.topWikiWidget,
div[id$='main'],
.inlineFrame,
div[id^='feedlyPart']{
padding:0!important;
}

#floatingBar{
padding: 5px 0 0 0!important;
}

.feedIndex{
padding-left: 34px!important;
}

.moreHandle{
font-size: x-small!important;
margin-left: 42px!important;
color: rgb(190,80,60)!important;
}

h1:not(:hover),
#feedlyFrame .feedTitle,
.moreHandle:hover{
color: rgb(255,150,0)!important;
}

.topWikiWidget,
div[id$='main'],
*[style*='margin-top'],
*[style*='margin-bottom'],
[class^='entry'] p{
margin-bottom: 0!important;
margin-top: 0!important;
}

#searchBoxPlaceholder{
margin-top: 3px!important;
}


#feedlyDialogFramePart div,
#feedlyPageHeader{
padding-top: 0!important;
}

.inlineFrame{
min-height: 0!important;
min-width: 0!important;
}

[class$='Entry'],
[class^='entry']{
max-height: none!important;
max-width: none!important;
}

#feedlyTabsHolder{
width: 220px!important;
}

#mainBar{
width: 100%!important;
}

#floatingBar,
#feedlyFrame{
margin-left: 220px!important;
}

.feedIndexTitleHolder,
div[class*='UnreadCount']{
max-height: 16px!important;
}

#feedlyTabs,
#feedlyDialogFramePart,#store {
overflow-y: hidden!important;
}

/* #quicklookContent,#title,#newCategories,#storeFrame,#formActions, */
#feedlyDialogFramePart div,
[id^='feedly'],
.area,
.panels,
.subscriptionContentsHolder div,
[class$='Entry'],
.u0Entry:hover,
[class$='Entry'] > div:not([class*='condensedTools']),
.u5EntryAnnotationHolder,
[class^='entry'],
.inlineFrame{
background-color: transparent!important;
background: none!important;
}

#fixedProfile,
.hAction, .subscriptionContentsHolder{
background-color: rgb(20,35,50)!important;
}

.hAction:hover{
color: #fff!important;
background-color: rgb(40,70,100)!important;
}

.read:hover,
.empty:hover,
.tab .label,
.entryTitle:hover{
color:rgb(70,150,200)!important;
}

.formContainer h1,
.formContainer,
.labelGroup span,
.read,
.empty,
.entryTitle,
#fullyLoadedFollowing{
color:rgb(100,130,160)!important;
}

.checkboxField:hover > .checkboxLabel,
.unread:hover,
.nonEmpty:hover,
.feedTitle:hover,
.tab .label:hover{
color:rgb(60,180,255)!important;
text-decoration: none!important;
}

.checkboxLabel,
.unread,
.nonEmpty,
#feedlyTabsHolder .feedTitle{
color:rgb(130,150,180)!important;
}

p,li,.entryBody .content{
color: #fff!important;
}

.inlineFrame{
transition-property: none!important;
/* z-index: 502 */
border-width: 2px!important;
border-radius: 9px!important;
margin: 4px 0 !important;
}

*:not(.inlineFrame){
border: none!important;
}

#feedlyTabsHolder .icon {
background-size: 21px 21px !important;
padding-right: 4px !important;
width: 21px !important;
height: 21px !important;
}

#floatingBar h1{
font-size: 26px!important;
}
}
