css:
@-moz-document url-prefix("http://myanimelist.net/animelist"),
               url-prefix("http://myanimelist.net/mangalist"),
               url-prefix("https://myanimelist.net/animelist"),
               url-prefix("https://myanimelist.net/mangalist")
{
	/* By: Adrien Pyke
	 * Original style by: Brad Conte;
	 */
	* {
		background-image: none !important;
		text-shadow: none !important;
		font-family: 'Open Sans',Verdana,Arial,sans-serif !important;
	}

	body {
		background-color: #F6F6F6 !important;
		color: #000000 !important;
		font-size: 12px !important;
		margin: 0px !important;
	}
	 
	/* Determines the positioning of your list
	*/
	#list_surround {
		margin: 0 auto !important;
		width: 950px !important;
	}
	#list_surround table {
		width: 100% !important;
		margin: 0px !important;
	}
	 
	/* All links on your list
	*/
	a {
		color: #333333 !important;
		text-decoration: none !important;
	}
	a:visited {
		color: #333333 !important;
		text-decoration: inherit !important;
	}
	a:hover {
		color: #333333 !important;
		text-decoration: underline !important;
	}
	 
	/* Alternating row color 1
	*/
	.td1 {
		background-color: #F6F6F6 !important;
		border-color: #C4C4C4 !important;
		border-style: solid !important;
		border-width: 0 !important;
	/* For column seperation:	border-width: 0 1px 0 0 !important; */
		color: #000000 !important;
		padding: 6px !important;
	}
	 
	/* Alternating row color 2
	*/
	.td2 {
		background-color: #E6E6E6 !important;
		border-color: #C4C4C4 !important;
		border-style: solid !important;
		border-width: 0 !important;
	/* For column seperation:	border-width: 0 1px 0 0 !important; */
		color: #000000 !important;
		padding: 6px !important;
	}
	/* Border for the "#" column.
	*/
	.td1:first-child,
	.td2:first-child,
	.table_header:first-child {
		border-right-width: 1px !important;
	}
	/* Border around the entire.
	 * Just do the sides, the top and bottom are implicit.
	*/
	.td1:first-child,
	.td2:first-child,
	.table_header:first-child {
		border-left: 1px solid #333333 !important;
	}
	.td1:last-child,
	.td2:last-child,
	.table_header:first-child {
		border-right: 1px solid #333333 !important;
	}
	 
	/* This represents the "Anime Title", "Score", "# Eps" columns
	*/
	.table_header {
		background-color: #333333 !important;
		border-color: #C4C4C4 !important;
		border-style: solid !important;
		border-width: 0 !important;
	/* For column seperation	border-width: 0 1px 0 0 !important; */
		color: #FFFFFF !important; 
		padding: 6px !important;
	}
	 
	.td1 div[style^='float'],
	.td2 div[style^='float'] {
		margin-left: 7px !important;
		/*padding-left: 1px;
		border-left: 1px solid #C4C4C4;*/
	}
	 
	/* headerLink represents the color of the links inside the_header
	*/
	.table_headerLink {
		color: inherit !important;
	}
	.table_headerLink:visited {
		color: inherit !important;
	}
	.table_headerLink:hover {
		color: inherit !important;
	}
	 
	/* Hack for the idiot borders on the "#" column header that are hard-coded in the . Borders are on all sides, but I don't want it like that, so make the unwanted borders match the background.
	*/
	td.table_header:first-child {
		border-top-color: #333333 !important;
		border-left-color: #333333 !important;
		border-bottom-color: #333333 !important;
		border-right-color: #c4c4c4 !important;
	}
	/* To keep the's right border from including the header. */
	td.table_header:last-child {
		border-right-color: #333333 !important;
	}
	 
	/* Controls the select form decoration (the drop down select box)
	*/
	.form {
		border-color: #000000 !important;
		border-style: solid !important;
		border-width: 1px !important;
		color: #000000 !important;
		font-family: Verdana,Arial !important;
		font-size: 12px !important;
		padding: 2px !important;
	}
	 
	/* Which 'status' up top is selected?
	*/
	.status_selected {
		background-color: #333333 !important;
		border-color: #333333 !important;
		border-style: solid !important;
		color: #ffffff !important;
		padding: 7px !important;
		font-weight: bold !important;
		width: 16.7% !important;
	}
	.status_selected a {
		color: inherit !important;
	}
	.status_not_selected {
		background-color: #e6e6e6 !important;
		border-color: #333333 !important;
		border-style: solid !important;
		color: #000000 !important;
		padding: 7px !important;
		width: 16.7% !important;
	}
	 
	/* Header classes for Currently Watching, Completed, Dropped, etc...
	*/
	.header_cw {
	}
	.header_completed {
	}
	.header_onhold {
	}
	.header_dropped {
	}
	.header_ptw {
	}
	.header_title {
		font-size: 22px !important;
		font-weight: bold !important;
		margin: 10px 0 2px 0 !important;
		color: #333333 !important;
	}
	.category_totals {
		background-color: #333333 !important;
		color: #ffffff !important;
		padding: 8px !important;
	}
	#grand_totals {
		margin: 15px 0 !important;
		font-size: 14px !important;
		text-align: center !important;
	}
	 
	/* controls what styles you can give to all the anime titles in your list
	*/
	.animetitle {
		font-weight: bold !important;
		color: #333333 !important;
	}
	 
	/* Formatting for the control strip
	 */
	#mal_control_strip {
		width: 100% !important;
		margin: 0px !important;
		left: 0px !important;
		font-variant: normal !important;
		text-transform: none !important;
		font-size: 11px !important;
	}
	#mal_control_strip td {
		height: 45px !important;
	}
	#mal_control_strip td div {
		margin: 3px 0px !important;
	}
	#mal_cs_listinfo {
		width: 75px !important;
		padding: 0px 1em !important;
	}
	#mal_cs_links {
		width: 150px !important;
		padding: 0px 1em !important;
	}

	#mal_cs_listinfo, #mal_cs_links, #mal_cs_pic {
	    background-color: #333333 !important;
	    border-right: 1px solid !important;
	    border-color: #C4C4C4 !important;
	    color: #FFFFFF !important; 
	}
	#mal_cs_otherlinks {
	    background-color: #333333 !important;
	    color: #FFFFFF !important;
	    padding: 0px 1em !important;
	}
	#mal_cs_listinfo a, #mal_cs_links a, #mal_cs_pic a, #mal_cs_otherlinks a {
		color: #FFFFFF !important; 
		text-decoration: underline !important;
	}
	/* Hide copyright and search
	*/
	#copyright, #mal_cs_powered {display: none !important}
}
