@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {


#searchArea {
right: 20px!important;
left:auto!important;
}

#searchDropdown {
Color:#000000!important;
}

#searchDropdown div.textTop {
    z-Index: 191 !important; 
}

#noTouch #searchDropdownOverlay {
    right: 55px!important;
    left:auto!important;
}

#noTouch #searchDropdownMenu {
    right: 55px!important;
    left:auto!important;
}

#noTouch #searchDropdownMenu div.sdmItem {
    line-height: 23px !important;
}

#appBar, #appFolderButtonIcon {
    left:-360px!important;
}

#appFolder, #moreUsageHint, #moreAdFrame {
    left:110px!important;
}

#appFolderShadow {
    left:90px!important;
}

#top-55 #rockdocklock #exitlink {
    background:url(data:image/gif;base64,R0lGODlhDAASAJEAAEBTSkFaYWh+cFlaSiH5BAAAAAAALAAAAAAMABIAQAIdhI+ZIaPH3ANx0mbr1I8rb22YKHWjWX6nmi6rVQAAOw%3D%3D) no-repeat 42px 7px!important;
    padding-left:5px!important;
}

#top-55 #rockdocklock #exitlink:hover {
    background:url(data:image/gif;base64,R0lGODlhDAASAJEAAEBTSkFaYWh+cFlaSiH5BAAAAAAALAAAAAAMABIAQAIdhI+ZIaPH3ANx0mbr1I8rb22YKHWjWX6nmi6rVQAAOw%3D%3D) no-repeat 42px 7px!important;
}

#top-55 #rockdock #menuHover span.userlink, #top-55 #rockdocklock #rockdock-message-count a, #top-55 #menuHover a {
    color:#96a19b!important;
}

#top-55 #rockdocklock #rockdock-message-count:hover a, #top-55 #menuHover:hover a {
    color:#afbfb6!important;
}
span[id="rockdocklock"] {
    margin-left: 4px! important;
}

#top-55 #rockdocklock a[href="http://my.deviantart.com/services/#subscription"] {
width:0px!important; font-size:0px!important;
}
}
