css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") 
{
header a
{
    background-image: url("data:image/jpeg;base64,") !important;
    background-position: center !important;
    color: transparent !important;
    background-size: 230px 203px !important;
    padding-bottom: 95px !important;
    border: 1px solid black !important;
    border-radius: 8px !important;
}

header
{
    padding-bottom: 100px !important;
}

a:visited
{
    color: rgb(34, 67, 117) !important;
}

body
{
    font-family: comic sans ms, verdana!important;
}

.obsolete a
{
    opacity: 0.2 !important;
}

header, nav
{
    background-image: linear-gradient(rgb(209, 238, 238), rgb(255, 236, 139)) !important;
}
}
