css:
/* i really want this to be global */
@-moz-document domain(porseshkade.com) {
#block-search-form .form-submit {
padding: .3em .6em !important;
background: #ffc000;
border: 2px solid #ffc000;
  color:#fff
}
#block-search-form .form-submit:hover {
padding: 0.3em 0.6em;
background: #ECB610;
border: 2px solid #ffc000;
  color:#fff
}
.f-reply-btn {
width: 76px;
height: 21px;
margin: 6px;
border-radius: 22px;
border: solid 2px #b2b5b9;
float: right;
}
.f-avatar {
padding: 2px;
float: right;
border: 1px solid #c3c3c3;
border-radius: 26px;
height: 26px;
width: 26px;
background-color: #fff;
margin-right: 6px;
}
.f-avatar img {
width: 26px;
height: 26px;
border-radius: 20px;
}

.show_violation_report {
float: right;
margin: 6px 2px 0px 2px;
border-radius: 24px;
background: #F5F5F5;
color: rgba(34,34,34,0.64);
cursor: pointer;
padding: 2px 9px;
font-size: 12px;
border: solid 2px #acacac;
}
.main-blue-background {
background: #fff !important;
direction: rtl;
}
.f-right-col {
width: 283px;
float: left;
background-color: #e2f1f9;
border: solid 2px #D4E7F1;
border-radius: 7px;
padding: 3px;
}
.f-a-collapsed, .kd-h-div{
background: #25a0ca;
padding: 8px 20px 8px 8px;
border-radius: 3px;
font-size: 12px;
width: 245px;
margin: 1px 0;
}
.f-a-count {
width: 22px;
height: 19px;
padding-top: 4px;
background: #25a0ca;
border-radius: 8px;
text-align: center;
float: left;
color: #fff;
border: 2px solid rgba(255, 255, 255, 0.3);
}
.f-tags-row {
height: 24px;
width: 954px;
padding: 6px 13px;
background-color: #00aeef;
border-radius: 3px;
position: relative;
}
.f-header1 {
height: 52px;
width: 100%;
background-color: #00aeef;
}
.f-logo {
float: left;
width: 272px;
padding: 0 0px 0 0;
}
.f-selector-div {
float: left;
width: 294px;
padding: 18px 26px 0 0;
}
.f-topic-item:hover, .f-kd-item:hover {
background-color: #edf5fa;
}
.ajax-link:hover {
color: #00aeef;
}
.internal-footer {
width: 100%;
border-radius: 11px;
background-color: #00aeef;
padding: 11px 0;
text-align: center;
margin: 3px 0;
direction: rtl;
color: #fff;
font-size: .7em;
}

.tdes {
margin: 0px 10px;
background: url(https://cdn1.iconfinder.com/data/icons/windows8_icons/26/error.png) no-repeat right;
padding: 8px 43px 0px 0;
line-height: 19px;
font-size: 12px;
}
.f-a-collapsed {
background: #39AED6 url(http://porseshkade.com/sites/all/themes/faq-darsnameh/images/t2.gif) no-repeat 259px 18px !important;
}
.f-a-count {
background: #39AED6 !important;
}
.f-a-collapsed:hover {
background: #25a0ca url(http://porseshkade.com/sites/all/themes/faq-darsnameh/images/t2d.gif) no-repeat 259px 18px !important;
cursor: pointer;
}
.notifications {
float: right;
background: url(https://cdn2.iconfinder.com/data/icons/freecns-cumulus/16/519589-087_Speechbubbles-16.png) no-repeat 0 2px;
min-width: 16px;
min-height: 20px;
cursor: pointer;
}
.f-wta {
background: url(https://cdn4.iconfinder.com/data/icons/wirecons-free-vector-icons/32/menu-alt-16.png) no-repeat 0px 0px;
float: left;
min-width: 23px;
min-height: 19px;
margin: 2px -9px 0;
cursor: pointer;
}
.t3 {
background: url(https://cdn2.iconfinder.com/data/icons/picol-vector/32/arrow_sans_up-16.png) no-repeat;
width: 16px;
height: 16px;
margin-top: -11px;
float: left;
margin-left: 8px;
}
.new-notifications-list .container {
padding: 0px 0px 0px 0;
border-radius: 3px;
border: solid 1px #184081;
background: #fff;
color: #000;
width: 238px;
margin-top: -25px;
max-height: 300px;
border-top: none;
}
.notification-item:hover {
background-color: #00aeef;
color: #fff;
}
.visited {
background-color: #fff;
}
.notification-item {
border-bottom: solid 0px #cdcdcd;
padding: 0px;
cursor: pointer;
  margin-right: 5px;
}
.f-pin-div {
float: right;
background: #EEEEEE;
border-radius: 19px;
color: #000;
padding: 0px 7px;
margin: 5px 0px;
cursor: pointer;
border: solid 2px #acacac;
}
.f-pin-title {
float: right;
padding-right: 0px;
padding-top: 0px;
padding-left: 0px;
font-size: 12px;
height: 0px;
}
.ajax-link:hover {
color: #C6E8F5;
}
.f-popular-tags li a:hover {
color: #C8F0FF;
}
.f-pin-cb {
float: left;
}
.f-user-menu {
float: right;
direction: rtl;
margin-right: 10px;
padding-top: 8px;
}
.f-left-col {
width: 653px;
float: left;
border: solid 2px #D4E7F1;
  border-left-width: 0px;
  border-radius: 0px;
-webkit-border-top-right-radius: 5px;
-webkit-border-bottom-right-radius: 5px;
-moz-border-radius-topright: 5px;
-moz-border-radius-bottomright: 5px;
border-top-right-radius: 5px;
border-bottom-right-radius: 5px;
background-color: #fff;
padding: 5px !important;
}
.f-right-col {
width: 283px;
float: left;
background-color: #e2f1f9;
border: solid 2px #D4E7F1;
  border-right-width: 0px;
border-radius: 0px;
  -webkit-border-top-left-radius: 5px;
-webkit-border-bottom-left-radius: 5px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-bottomleft: 5px;
border-top-left-radius: 5px;
border-bottom-left-radius: 5px;
padding: 3px;
}
}
