	border: 0;
	background-color: #f1ecc4;
	background-image: url('http://oreeme-fan.at.ua/Forum_Oreeme/oreeme-forum_2/back_light.jpg');
}h1 {
	color: white;
	font-family: "Lucida Grande", "Trebuchet MS", Verdana, sans-serif;
	font-weight: bold;
	font-size: 1.8em;
	text-decoration: none;
}

th {
	color: #FFA34F;
	font-size: 1.1em;
	font-weight: bold;
	background-color: #1f1f1f;
	background-image: url('http://oreeme-fan.at.ua/Forum_Oreeme/oreeme-forum_2/cellpic3.gif');
	white-space: nowrap;
	padding: 7px 5px;
}


* {
	
	margin: 0;
	padding: 0;
}

html {
	font-size: 100%;
}

body {
	
	font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;
	color: #000000;
	background-color: #FFFFFF;
	font-size: 62.5%; 
	margin: 0;
}



.menuTd {padding-left:12px;padding-right:10px; background:url("http://s019.radikal.ru/i631/1207/99/29078b2e4177.jpg") repeat-y scroll right top transparent;}






.rtl a {
	direction: rtl;
	unicode-bidi: embed;
}


th a,
th a:visited {
	color: #000000 !important;
	text-decoration: none;
}

th a:hover {
	text-decoration: underline;
}.quotetitle, .attachtitle {
	margin: 10px 5px 0 5px;
	padding: 4px;
	border-width: 1px 1px 0 1px;
	border-style: solid;
	border-color: #000000;
	color: #333333;
	background-color: #c6a16c;
	font-size: 0.85em;
	font-weight: bold;
}

.quotecontent, .attachcontent {
	margin: 0 5px 10px 5px;
	padding: 5px;
	border-color: #000000;
	border-width: 0 1px 1px 1px;
	border-style: solid;
	font-weight: normal;
	font-size: 1em;
	line-height: 1.4em;
	font-family: "Lucida Grande", "Trebuchet MS", Helvetica, Arial, sans-serif;
	background-color: #e6c9a1;
	color: #4B5C77;
}


.codetitle {
	margin: 10px 5px 0 5px;
	padding: 2px 4px;
	border-width: 1px 1px 0 1px;
	border-style: solid;
	border-color: #000000;
	color: #333333;
	background-color: #c6a16c;
	font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;
	font-size: 0.8em;
}

.codecontent {
	direction: ltr;
	margin: 0 5px 10px 5px;
	padding: 5px;
	border-color: #000000;
	border-width: 0 1px 1px 1px;
	border-style: solid;
	font-weight: normal;
	color: #4B5C77;
	font-size: 0.85em;
	font-family: Monaco, 'Courier New', monospace;
	background-color: #e6c9a1;
}
}
