css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

#home_button.tab.iconic > a,
#home_button.tab.iconic.selected > a {
  background-image: url("http://media.tumblr.com/de615f7bd82f3a01254f3913146133fc/tumblr_inline_mvxkwhdnXR1rxnozk.png") !important;
  background-size: 40px 40px !important;
  background-repeat: no-repeat !important;
  padding-right: 10px !important;
  opacity: .65 !important;
}
#home_button.tab.iconic > a:hover {
  opacity: .85 !important;
  -moz-transition: ease-out all .25s !important;
}
#home_button.tab.iconic.selected > a {
  opacity: 1 !important;
  -moz-transition: ease-out all .25s !important;
}
#home_button.tab.iconic > .tab_anchor::before {
	content: none !important;
}

.l-header-container--refresh .tab_bar .tab .tab_anchor {
  height: 40px
  }

.identity-refresh .selected .tab_notice, .identity-refresh .new_post_notice_container {

       margin-left: 15px !important;
}
}
