css:
@-moz-document url-prefix("https://publichd.se/index.php") {
html {
display:inline;
}
#logo {
height: 75px;
width: 100%;
}
table {
height: 0px !important;
}
.block-head-title {
display: none;
opacity: 0;
}
#menu {
height: 0px;
position: relative;
top:17px;
}
.block-head {
background-color: #313131;
}
.block-head-title {
background-color: #313131;
}
.b-content {
background-color: #313131 !important;
}
.torrent-search li {
line-height: 1.2em;
margin: 0.2em 0;
}
#bodyarea #mcol .block .header {
background-color: rgb(65, 65, 65);
}
td.lista {
background-color: rgb(65, 65, 65);
color: white !important;
}
.header2 {
background-color: rgb(95, 95, 95);
}
a:link {
color: white !important;
}
a:visited {
color: white !important;
}
input[type='text']{
width: 250px;
position:relative;
left:0;
}
input[type='submit']{
width: 160px;
}
.pager{
background-color: #3E3E3E;
}
.pagercurrent {
background-color: rgb(95, 95, 95);
}
td {
color:white;
}
.header span, .header2 font  {
color: white !important
}
.footer {
display:none;
}
#footer {
position:relative;
bottom: 0;
align: center;
width: 100%;
}
select.drop_pager {
border: 0px;
height: 20px;
color: white;
font-weight:bold;
}
#torrbg2 {
display:none;
}
}
