css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nzbfinder.ws") {
  html, body {
    color: #ddd !important;
    background-color: #111 !important;
    text-shadow: none !important;
  }
  a {
    color: #69c !important;
    background-color: transparent !important;
    text-shadow: none !important;
  }
  a:hover {
    color: #7af !important;
    background-color: transparent !important;
  }
  li.active a {
    color: #cef !important;
    font-weight: bold !important;
  }
  div.well {
    background-color: #151515 !important;
    color: inherit !important;
    border: none !important;
  }
  .alert-info {
    background-color: #222 !important;
    text-shadow: none !important;
  }
  div tr:nth-child(odd), div tr:nth-child(odd) * {
    background-color: #15151A !important;
  }
  div tr:nth-child(even), div tr:nth-child(even) * {
    background-color: transparent !important;
  }
  footer {
    background-color: #111 !important;
    border-top: 1px solid #555 !important;
  }
  select, input, textarea {
    color: #eee !important;
    background-color: #000 !important;
    border: 1px solid #444 !important;
  }
  .btn, #headsearch_go, input[type="submit"], input[type="button"], input[type="reset"], button {
    color: #fff !important;
    background: #555 !important;
    text-shadow: none !important;
  }
  div.pricingtable, div.navbar-inner {
    background: #222 !important;
  }
  div.container-fluid div.span4 {
    background-color: #222 !important;
    border: 1px solid #444 !important;
  }
  ul.dropdown-menu {
    background-color: #333 !important;
    border: 1px solid #444 !important;
    color: #aef !important;
  }
  div.alert, div.alert h4 {
    background-color: #333 !important;
    color: #aef !important;
    border: none !important;
  }
  ul.dropdown-menu li.divider {
    background-color: #888 !important;
    border-bottom: 1px solid #888 !important;
  }
  input.btn.nzb_multi_operations_download {
    background: #3a5 !important;
  }
  input.btn.nzb_multi_operations_cartdelete {
    background: #c22 !important;
  }
  input.btn.nzb_multi_operations_cart {
    background: #aa2 !important;
  }
}
