css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/login'),
  url('http://www.tumblr.com/dashboard'),
  url('http://www.tumblr.com/new/blog'),
  url('http://www.tumblr.com/inbox'),
  url('http://www.tumblr.com/help'),
  url('https://www.tumblr.com/preferences'),
  url('http://www.tumblr.com/lookup'),
  url('http://www.tumblr.com/explore'),
  url('http://www.tumblr.com/following'),
  url('http://www.tumblr.com/likes'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {



*{
      text-shadow: none !important;
    }
    
    
    .controls_section li a {
      color: black !important;
    }
body {
background:url("http://i.imgur.com/iGjFXwz.jpg") no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;
  background-size: 100% 100%  !important;
}



#logo {
  width: 0px !important;
  height: 0px !important;

  width: 196px !important;
  height: 44px !important;
}



img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
opacity: 1.0 !important;  
}
#new_post{
  background-image: url(https://secure.assets.tumblr.com/images/post_icons_sprite.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;
}



new_post {
  background-color: rgba(255, 255, 255, 0.25) !important;
}



#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(178, 208,225, 0.3) !important;
  
}
 


#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 0.25) !important;
}


   
    .post_content,
    .new_post_label,
    #right_column > UL:first-child > LI:first-child > A > SMALL > DIV,
    .post_controls A,
    .post_info,
    .post_info A,
    .footer_links.with_tags.with_source_url A,
    .footer_links A,
    .footer_links,
    .block,
    #dashboard_controls A,
    #dashboard_controls DIV SPAN,
    .add_and_remove,
    .notification.single_notification,
    .notification,
    .notification .hide_overflow A,
    .notification.last_notification,
    .notification.last_notification A,
    .answer_container SPAN,
    .notes,
    .notes LI,
    .notes_hide_link A,
    .read_more_container,
    #footer, #footer DIV A,
    .post_question_asker,
    .source_url,
    .label_and_follow,
    .follow, #search_query, #search_controls_button,
    .posts, .followers, .messages, .drafts, .queue, .activity, .processing, .members, .flat.no_hover DIV,
    A[class^="category"],
    a:link,
    a[href^="http"],
    a[href^="/edit"],
    a[href^="/likes"],
    a[href^="/new"],
    a[href^="/tagged"],
    a[href^="/login"],
    a[href^="/docs"],
    a[href^="/explore"],
    a[href^="/mega-editor"],
    a[href^="/spotlight"],
    .count, .customize, .settings,
    a span[id^="note_link"],
    .answer_container,
    .no_posts_found,
    .following, H1[id="page_heading"], div.l-content P, H1, p SMALL,
    .notes_loader,
    .small_links A .hide_overflow, .sorry P, .cell.lead_editor, .sidebar_nav_item A, .tag,
     H2, #posts .post .post_controls .reblog_count span, .open_blog.with_subtitle .hide_overflow a,
    .settings_container .left_column {
color: rgba(0,0,0,1.0) !important;
    }
    a:link:hover,
    A[href^="http"]:hover,
    a[href^="/edit"]:hover,
    a[href^="/likes"]:hover,
    a[href^="/new"]:hover,
    a[href^="/tagged"]:hover,
    a[href^="/customize"]:hover,
    a[href^="/explore"]:hover,
    a[href^="/mega-editor"]:hover,
    a[href^="/spotlight"]:hover,
    a[id^="ask_answer"]:hover,
    a span[id^="note_link"]:hover,
    .block_link:hover,
    .following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,
    SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover, #footer DIV STRONG:hover, .activity:hover, .processing:hover, .members:hover,
    BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,
    #posts .post .post_controls .reblog_count span:hover,.open_blog.with_subtitle .hide_overflow a:hover {
color: rgba(0,0,0, 1.0) !important;
    }
    .source_url_gradient, #toggle_reasons {
      display: none !important;
    }
    .notification.single_notification A,
    .notification.first_notification A,
    .notification A,
    .notification.last_notification A,
    .notification .hide_overflow A {
      border-bottom: solid thin rgba(66,87,106, 1.0) !important;
    }
    .post_content P A,
    .post_title A {
      color: rgba(64,64,64,1.0) !important;
    }
    

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}



li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}


.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}
