css:
@-moz-document domain("stri.ms") {
.span7 { 
width: 720px;margin-left: 50px;
}[class*="span2"] { 
float: left;
margin-left: 0px;
min-height: 1px;
background:#f1f4f6;
padding:10px;-webkit-border-radius: 6px;
 border-radius: 6px;border:1px solid #dfe7ed;
}
.span2 ul li a {color:#000;padding:11px 0 11px 0;font:12px Arial;}
.span2 ul  {color:#000;margin: 0px 0px 10px 0px;}
.span2 ul li {color:#000;padding:3px 0 3px 5px;background:#cdd9e2;margin:3px 0 3px 0;-webkit-border-radius: 6px;
 border-radius: 6px;}
 
 .span2 h5 {text-align:center;margin:0 0 5px; color:#1c3647;}
 
 .content-row {background:#f8f7f6;padding:11px;border-left:5px solid #cde0ef;-webkit-border-radius: 6px;
 border-radius: 6px;}
 ul.content  {background:#ffffff;margin:4px 0 0 0;padding:7px;-webkit-border-radius: 6px;
 border-radius: 6px;border:1px solid #e8e0da;}
 .media, .media .media {margin:2px 0 0 -10px;padding:5px 0 0 0;border-bottom:1px solid #f6f0ea;}
 p.comments_addons {font:11px Arial;color:#000;}
  p.comments_addons a {font:11px Arial;color:#ae9e91;}
  p.comment_text {border-top:1px solid #e6ded8;margin-top:3px;padding:3px 0 0 0;font:12px Arial;}
  .row h4 {font:bold 17px Arial;}
  .content-row .voting { 
float: left;
width: 60px;margin-left:10px;
}
  .media-heading {border-bottom:1px solid #e2dedb;padding:0px 0 4px 0;}
  hr {margin:6px 0 6px 0;}
.media-body {font:12px Arial;color:#949494;}
.media-body a {color:#6c6c6c;}


.span2 table.table .btn {display:none;}
h2 { 
font-size: 14.5px;
}
.table th, .table td { 
border-top: none;
}
}
