css:
@-moz-document domain("stri.ms") {
.span7 { 
width: 720px!important;margin-left: 50px!important;
}[class*="span2"] { 
float: left;
margin-left: 0px!important;
min-height: 1px!important;
background:#171717!important;
padding:10px!important;-webkit-border-radius: 6px!important;
 border-radius: 6px!important;border:1px solid #000!important;
}
.span2 ul li a {color:#b0b0b0!important;padding:11px 0 11px 0!important;font:12px Arial!important;}
.span2 ul  {color:#000!important;margin: 0px 0px 10px 0px!important;}
.span2 ul li {color:#000!important;padding:3px 0 3px 5px!important;background:#343434!important;margin:3px 0 3px 0!important;-webkit-border-radius: 6px!important;
 border-radius: 6px!important;}
 
 .span2 h5 {text-align:center!important;margin:0 0 5px!important; color:#9e9e9e!important;}
 
 .content-row {background:#171717!important;padding:11px!important;border-left:5px solid #000!important;-webkit-border-radius: 6px!important;
 border-radius: 6px!important;}
 ul.content  {background:#000!important;margin:4px 0 0 0!important;padding:7px!important;-webkit-border-radius: 6px!important;
 border-radius: 6px!important;border:1px solid #222222!important;}
 .media, .media .media {margin:2px 0 0 -10px!important;padding:5px 0 0 0!important;border-bottom:1px solid #3e3e3e!important;}
 p.comments_addons {font:11px Arial!important;color:#000!important;}
  p.comments_addons a {font:11px Arial!important;color:#ae9e91!important;}
  p.comment_text {border-top:1px solid #222222!important;margin-top:3px!important;padding:3px 0 0 0!important;font:12px Arial!important;}
  .row h4 {font:bold 17px Arial!important;color:#bdbdbd!important;}
  .content-row .voting { 
float: left!important;
width: 60px!important;margin-left:10px!important;
}
  .media-heading {border-bottom:1px solid #3e3e3e!important;padding:0px 0 4px 0!important;}
  hr {margin:6px 0 6px 0!important;border-bottom: 1px solid #000!important;
border-left: 0px none currentColor!important;
border-right: 0px none currentColor!important;
border-top: 1px solid #000!important;}
.media-body {font:12px Arial!important;color:#f!important;}
.media-body a {color:#6c6c6c!important;}
.span2 table.table .btn {display:none!important;}
body {background-color:#2e2e2e!important;color:#e1e1e1!important;}
.btn {background:#545454!important;text-shadow:none!important;border:none!important;color:#b1b1b1!important;}
textarea {background:#000!important;border:1px solid #000!important;}
a {color:#9c9c9c!important;}
.table th, .table td { 
border-top: none!important;
}h2 { 
font-size: 14.5px!important;
color:#fff!important;
}
h3 {color:#e1e1e1!important;}
.well {background:#000!important;border:none!important;}.dropdown-menu { background:#000!important;}#newNotice li {margin:5px 15px 5px 15px!important;border-bottom:1px solid #151515!important;}.dropdown-menu .divider { display:none!important;
}
}
