css:
@-moz-document domain("twitter.com") {
.front-signin{
  position:fixed !important;
  top:49% !important;
  left:37.3% !important;
  background:rgb(233,233,233) !important;
  border: 1px solid rgb(190,190,190) !important;
}
.front-card{
  position:fixed;
  width:300px !important;
  height:300px !important;
  margin:-300px 0 0 -165px !important;
  background-image:url(http://i.imgur.com/osQoxbv.jpg) !important;
}
.front-welcome, .front-signup, .footer, .larry-topbar, i.topbar-divider,.topbar-tweet-btn, .js-wtf-module,  .site-footer,.bird-topbar-etched,#user-dropdown-toggle span.caret,li.profile i.nav-me,
#user-dropdown li.dropdown-divider,.dropdown-menu li.current-user,.nav-home,.nav-people,.nav-topics{
  display:none !important;  
}
body *{
box-shadow:none!important;
text-shadow:none!important;
}
.primary-btn, .following.first-hover .follow-btn:hover, .following .follow-btn, .following .follow-button.cancel-
hover-style:hover, .following .follow-button, .front-welcome, .btn:hover, .photo-selector:not(.disabled):hover .btn,
.follow-combo.open .btn-user-actions, .user-dropdown.open, .btn, .dropdown-menu li > a:hover, 
.dropdown-menu .dropdown-link:hover, .dropdown-menu .selected a, .dropdown-menu .dropdown-link.selected {
  background-image:none !important;
}
.nav  {
  float:right !important;
  margin:0 !important;
}
.pull-right{
  margin-right: 10px !important;
  float: left !important;
}
#close-all-button{
  right:76% !important;
}
.dashboard  {
  float:right !important;
  width:32% !important;
}
.content-main  {
  float:left !important;
  width:67% !important;
}
.wrapper,.container{
  width:80% !important;
}
.account, .interest-category, .list, .saved-search, .tweet, .app, .discover-item  {
  padding:5px 50px 5px 10px !important;
}
.tweet-box{
  width:93%  !important;
}
.header-inner{
  padding:9px 12px !important;
}
.account, .interest-category, .list, .saved-search, .tweet, .app, .discover-item,.stream-item-activity-me{
  border-bottom:none !important;
}
.stream-end{
  border-top:1px solid rgba(0, 0, 0,0.1) !important;
}
.stream-end-inner{
  padding:5px 10px !important;
}
.nav li#user-dropdown{
  position:fixed!important;
  left:85.4%!important;
}
#global-actions.nav{
  margin-right:60px!important;
}
#global-actions li a {
  padding: 13px 17px!important;
}
.discover-item .expanded-content {
    margin:0!important;
}
.btn-search-actions  {
    margin-top:5px!important;
}
.replies .tweet{
    padding-left:70px!important;
}
#global-actions .nav-new{
margin-left:0!important;
}
}
