css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codeskulptor.org")

{

/*text&background*/

html, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, footer, fieldset

{
color: white !important;
background: black !important;
}

select, button, div

{
background: black !important;
}

a

{
background-color: black !important;
}

select, input, code, textarea, pre, label, div, dd, option, .cm-variable, .cm-operator

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, input, #console pre, .CodeMirror-selected

{
background: #333333 !important;
}

}
